-- Name: cards(); Type: FUNCTION; Schema: mne_sabredav; Owner: admindb
--

CREATE FUNCTION mne_sabredav.cards() RETURNS trigger
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
  acttime INTEGER;
  r RECORD;
  r_userid VARCHAR;
  r_id VARCHAR;
  r_contactid INTEGER;
  r_vcard VARCHAR;
  r_str VARCHAR;
  r_personid VARCHAR;
  r_match VARCHAR;
BEGIN

  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  
  IF TG_OP = 'INSERT' OR TG_OP = 'UPDATE' THEN
    r_vcard = NEW.carddata;
  ELSE
    r_vcard = OLD.carddata;
  END IF;
  
  r_vcard = regexp_replace(r_vcard, E'\r',   E'\n',  'g');
  r_vcard = regexp_replace(r_vcard, E'\n\n', E'\n',  'g');
  r_vcard = regexp_replace(r_vcard, E'\n[ \t]*', E'\n', 'g');
  
  IF TG_OP = 'INSERT' THEN

    SELECT mne_catalog.mk_id() INTO r_id;
    SELECT loginname, personid INTO r_userid, r_personid FROM mne_personnal.personowndata WHERE personid = NEW.addressbookid;
       

      VALUES ( NEW.uri, r_id, acttime, r_userid, acttime, r_userid );
      
    FOR r IN SELECT regexp_split_to_table(r_vcard, E'\n') AS str LOOP
      IF substr(r.str, 1, strpos(r.str, ':')) = 'N:' THEN
        r_match = r.str;
        EXIT;
      END IF;
    END LOOP;
    
    IF r_match IS NULL THEN
      r_vcard = regexp_replace(r_vcard, 'UID:[^\n]*', 'UID:' || r_id || '@erpserver_c');
        VALUES ( r_id, 'no name', r_personid, acttime, r_userid, acttime, r_userid );
        VALUES ( r_id, '', '', '', '', 'de', '', '', acttime, r_userid, acttime, r_userid );
    ELSE
      r_vcard = regexp_replace(r_vcard, 'UID:[^\n]*', 'UID:' || r_id || '@erpserver_p');
        VALUES ( r_id, 'no', 'name', r_personid, acttime, r_userid, acttime, r_userid );
        VALUES ( r_id, '', '', '', '', '', '', acttime, r_userid, acttime, r_userid );
    END IF;
    
    IF mne_crm.vcard_parse(r_vcard) != 'ok' THEN
      DELETE FROM mne_crm.companydata WHERE companydataid = r_id;
      DELETE FROM mne_crm.company WHERE companyid = r_id;
      DELETE FROM mne_crm.persondata WHERE persondataid = r_id;
      DELETE FROM mne_crm.person WHERE personid = r_id;
    END IF;
       
  ELSIF TG_OP = 'UPDATE' THEN
      
    PERFORM mne_crm.vcard_parse(r_vcard);

  ELSIF TG_OP = 'DELETE' THEN

    SELECT loginname INTO r_userid FROM mne_personnal.personowndata WHERE personid = OLD.addressbookid;

    FOR r IN SELECT regexp_split_to_table(r_vcard, E'\n') AS str LOOP
      IF substr(r.str, 1, strpos(r.str, ':')) = 'UID:' THEN
        r_str = substr(r.str,strpos(r.str, ':' ) + 1);

        IF position('erpserver' in r_str) > 1 THEN
          r_personid = substr(r_str, 1, strpos(r_str, '@') -1 );

          PERFORM id FROM mne_crm.cardsinactive WHERE id = r_personid AND userid = r_userid;
          IF NOT FOUND THEN
          ELSE
            UPDATE mne_crm.cardsincative 
            SET
              noactive = true,
              modifydate = acttime,
              modifyuser = session_user
            WHERE
              id = r_personid AND userid = r_userid;
          END IF;
        ELSE
          RAISE WARNING 'no erp contact %', E'\r\n' || r_vcard;
        END IF;
      EXIT;
      END IF;
    END LOOP;

  END IF;
  
  RETURN NULL;
END $$;


