CREATE FUNCTION mne_shipment.deliverynote_invoice(p_deliverynoteid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ 
DECLARE 
    acttime INTEGER;
    p_invoiceid TEXT;
    p_invoicenumber INTEGER;
    p_num INTEGER;
    p_ownerid TEXT;
    p_delivered BOOL;
    p_lumpsum INTEGER;
    p_contactid VARCHAR;
    p_orderid VARCHAR;

    ref_invoiceid TEXT;
    r_withtimesheet BOOLEAN;
BEGIN
    
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    SELECT delivered, orderid INTO p_delivered, p_orderid FROM mne_shipment.deliverynote
    WHERE  deliverynoteid = p_deliverynoteid;

    IF p_delivered <> true THEN
        RAISE EXCEPTION '#mne_lang#Lieferung ist noch nicht erfolgt#';
    END IF;

    FOUND := false;
    SELECT invoiceid INTO p_invoiceid FROM mne_shipment.deliverynote
    WHERE  invoiceid IS NOT NULL AND deliverynoteid = p_deliverynoteid;

    IF FOUND THEN
        RAISE WARNING '#mne_lang#Lieferschein ist schon einer Rechnung zugeordnet#';
        return p_invoiceid;
    END IF;

    FOUND := false;
    SELECT i.invoiceid INTO p_invoiceid
    FROM mne_shipment.invoice i, mne_shipment.invoicemanagement im, mne_shipment.deliverynote d1, mne_shipment.deliverynote d2
    WHERE  im.invoiced = false 
       AND im.invoicemanagementid = i.invoiceid
       AND i.invoiceid = d1.invoiceid
       AND d1.orderid  = d2.orderid
       AND d2.deliverynoteid = p_deliverynoteid 
      AND  i.num = ( select MAX(num) from mne_shipment.invoice ii WHERE ii.invoiceid = d1.invoiceid);

    IF FOUND THEN
        FOUND := false;
        RAISE WARNING '#mne_lang#Ergänze letzte Rechnung#';
        
        UPDATE mne_shipment.deliverynote
        SET modifyuser = session_user, modifydate = acttime, invoiceid = p_invoiceid
        WHERE deliverynoteid = p_deliverynoteid AND invoiceid IS NULL;

    ELSE
        FOUND := false;
        SELECT DISTINCT max(num) + 1 INTO p_num FROM mne_shipment.invoice;
        IF NOT FOUND OR p_num IS NULL THEN
            p_num := 1;
        END IF;
 
        FOUND := false;
        SELECT DISTINCT i.ownerid, i.invoiceid, o.lumpsum INTO p_ownerid, ref_invoiceid, p_lumpsum
        FROM   mne_shipment.invoice i, mne_shipment.deliverynote d1, mne_shipment.deliverynote d2, mne_crm.order o
        WHERE  i.invoiceid = d1.invoiceid
          AND  d1.orderid = d2.orderid
          AND  o.orderid = d1.orderid
          AND  d2.deliverynoteid = p_deliverynoteid 
          AND  i.num = ( SELECT MAX(ii.num)
                         FROM   mne_shipment.invoice ii, mne_shipment.deliverynote dd
                         WHERE      ii.invoiceid = dd.invoiceid
                                AND d2.orderid = dd.orderid
                         GROUP BY dd.orderid);


        p_contactid = NULL;
        IF NOT FOUND THEN
            FOUND := false;
            SELECT d.ownerid, NULLIF(d.contactid,''), o.lumpsum INTO p_ownerid, p_contactid, p_lumpsum
            FROM mne_shipment.deliverynote d, mne_crm.order o
            WHERE d.deliverynoteid = p_deliverynoteid AND d.orderid = o.orderid;
            SELECT FALSE INTO r_withtimesheet;
        ELSE
            SELECT withtimesheet INTO r_withtimesheet FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = ref_invoiceid;
        END IF;    

       SELECT INTO p_invoiceid mne_catalog.mk_id();

        /* Tabelle invoice füllen */
        /* ====================== */

          createdate, createuser, modifydate, modifyuser )
        VALUES
            ( p_invoiceid, p_num, p_ownerid, 'textstd', 'condstd',
              acttime, session_user, acttime, session_user );


        SELECT mne_catalog.mk_id(), p_invoiceid, refid, companyid, personid, false, acttime, session_user, acttime, session_user 
        FROM mne_shipment.invoiceref WHERE invoiceid = ref_invoiceid;
                 
        IF p_contactid IS NOT NULL THEN
        END IF;
                   
        SELECT 
          mne_catalog.mk_id(), p_invoiceid, t0.timeid, acttime, session_user, acttime, session_user
        FROM mne_personnal.time t0 
          INNER JOIN mne_personnal.orderproducttime t1 ON ( t0.orderproducttimeid = t1.orderproducttimeid )
          INNER JOIN mne_crm.orderproduct t2 ON ( t1.orderproductid = t2.orderproductid  AND t2.orderid = p_orderid )
          LEFT JOIN mne_shipment.invoicetime t3 ON ( t3.timeid = t0.timeid )
          INNER JOIN mne_shipment.deliverynoteproduct t4 ON ( t2.orderproductid = t4.orderproductid )
          INNER JOIN mne_shipment.deliverynote t5 ON ( t4.deliverynoteid = t5.deliverynoteid AND t5.deliverynoteid = p_deliverynoteid )
        WHERE t3.timeid IS NULL AND t5.invoiceid IS NULL;    
                 
        UPDATE mne_shipment.deliverynote
        SET modifyuser = session_user, modifydate = acttime, invoiceid = p_invoiceid
        WHERE deliverynoteid = p_deliverynoteid AND invoiceid IS NULL;

        
    END IF;

   return p_invoiceid;
END; $$;


