CREATE FUNCTION mne_shipment.invoice_del(p_invoiceid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    p_invoiced BOOL;
    i INTEGER;
    p_id TEXT;
BEGIN

  SELECT INTO p_invoiced invoiced FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = p_invoiceid;
  IF p_invoiced = true THEN
    RAISE EXCEPTION '#mne_lang#Die Rechnung ist erfolgt und kann nicht gelöscht werden#';
  END IF;

  SELECT INTO p_id invoiceid FROM mne_shipment.invoice WHERE num = ( SELECT MAX(num) FROM mne_shipment.invoice );
  IF p_invoiceid <> p_id THEN
    RAISE EXCEPTION '#mne_lang#Die Rechnungsnummer ist nicht die Grösste und kann nicht gelöscht werden#';
  END IF;

  UPDATE mne_shipment.deliverynote SET invoiceid = NULL WHERE invoiceid = p_invoiceid;

  DELETE FROM mne_shipment.invoiceref WHERE invoiceid = p_invoiceid;
  DELETE FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = p_invoiceid;
  DELETE FROM mne_shipment.invoicetime WHERE invoiceid = p_invoiceid;
  DELETE FROM mne_shipment.invoice WHERE invoiceid = p_invoiceid;

  return 'ok';
END;
 $$;


