--
-- Name: invoice_ok(character varying, character varying, character varying, character varying, boolean, double precision, integer); Type: FUNCTION; Schema: mne_shipment; Owner: admindb
--

CREATE FUNCTION mne_shipment.invoice_ok(p_invoiceid character varying, p_ownerid character varying, p_textid character varying, p_condid character varying, p_withtimesheet boolean, p_paysum double precision, p_invoicetime integer) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    i INTEGER;
    p_invoiced BOOL;
    r_withtimesheet BOOL;
    r RECORD;
BEGIN

  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  SELECT invoiced, withtimesheet INTO p_invoiced, r_withtimesheet FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = p_invoiceid;
  SELECT * INTO r FROM mne_shipment.invoice t0 LEFT JOIN  mne_shipment.invoicemanagement t1 ON ( t0.invoiceid = t1.invoicemanagementid ) WHERE t0.invoiceid = p_invoiceid;

  IF p_invoiced = true AND ( r.textid <> p_textid OR r.condid <> p_condid OR r_withtimesheet <> p_withtimesheet OR r.invoicetime <> p_invoicetime ) THEN
    RAISE EXCEPTION '#mne_lang#Die Rechnung ist erfolgt und kann nicht geändert werden#';
  ELSE
    UPDATE mne_shipment.invoice SET ownerid = NULLIF(p_ownerid,''), textid = p_textid, condid = p_condid, modifydate = acttime, modifyuser = session_user
    WHERE  invoiceid = p_invoiceid;
  END IF;

  UPDATE mne_shipment.invoicemanagement SET withtimesheet = p_withtimesheet, invoicetime = p_invoicetime WHERE invoicemanagementid = p_invoiceid;
  
  PERFORM * FROM mne_shipment.invoiceref WHERE invoiceid = p_invoiceid;
  IF FOUND AND p_paysum != 0.0 THEN
    RAISE WARNiNG '#mne_lang#Bitte bezahlte Summe bei den Rechnungskontakten eintragen';
    RETURN p_invoiceid;
  END IF;
  
  SELECT DISTINCT INTO r
    t0."invoiceid" AS invoiceid,
    t4."withvat" AS withvat,
    t6."count" AS costumercount,
    COALESCE(t6.count,1.0) AS rescostumercount,
    SUM(t2.count * (t3.productprice * COALESCE(t5.rate_purchase,1.0))) / COALESCE(t6.count,1.0) AS netprice,
    SUM(t2.count * (t3.productprice * COALESCE(t5.rate_purchase,1.0))) AS fullnetprice,
    SUM(t4.withvat * (COALESCE(t3.productvat, (select vat from mne_crm.productdefault)))) AS resvat,
    SUM(t4.withvat * (COALESCE(t3.productvat, (select vat from mne_crm.productdefault))) / 100 * t2.count * (t3.productprice * COALESCE(t5.rate_purchase,1.0))) / COALESCE(t6.count,1.0) AS resabsvat,
    SUM((1 + t4.withvat * (COALESCE(t3.productvat, (select vat from mne_crm.productdefault))) / 100) * t2.count * (t3.productprice * COALESCE(t5.rate_purchase,1.0))) / COALESCE(t6.count,1.0) AS grossprice,
    t5."currency" AS currency
  FROM
    (((((((mne_shipment.invoice t0 LEFT JOIN mne_shipment.deliverynote t1 ON ( t0.invoiceid = t1.invoiceid) )
    LEFT JOIN mne_shipment.deliverynoteproduct t2 ON ( t1.deliverynoteid = t2.deliverynoteid) )
    LEFT JOIN mne_crm.orderproduct t3 ON ( t2.orderproductid = t3.orderproductid) )
    LEFT JOIN mne_crm.order t4 ON ( t3.orderid = t4.orderid) ))
    LEFT JOIN mne_base.currency t5 ON ( t3.productcurrencyid = t5.currencyid))
    LEFT JOIN mne_shipment.invoicerefcount t6 ON ( t0.invoiceid = t6.invoiceid))
  WHERE t0.invoiceid = p_invoiceid
  GROUP BY t0.invoiceid, t4.withvat, t6.count, t5.currency, t5.rate_purchase;

  PERFORM invoicepaid FROM mne_shipment.invoicepaid WHERE invoiceid = p_invoiceid AND invoicerefid IS NULL;
  IF FOUND THEN
    UPDATE mne_shipment.invoicepaid SET paysum = p_paysum, modifydate = acttime, modifyuser = session_user
    WHERE  invoiceid = p_invoiceid  AND invoicerefid IS NULL;
  ELSE
  END IF;

  IF r.grossprice <= p_paysum THEN
    UPDATE mne_shipment.invoicemanagement SET paid = true, modifydate = acttime, modifyuser = session_user
    WHERE  invoicemanagementid = p_invoiceid;
  ELSE
    UPDATE mne_shipment.invoicemanagement SET paid = false, modifydate = acttime, modifyuser = session_user
    WHERE  invoicemanagementid = p_invoiceid;
  END IF;

  return p_invoiceid;
END;
 $$;


