-- Name: invoice_payed(character varying); Type: FUNCTION; Schema: mne_shipment; Owner: admindb
--

CREATE FUNCTION mne_shipment.invoice_payed(p_invoiceid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    i INTEGER;
    p_invoiced BOOL;
    r_refid VARCHAR;
BEGIN

  FOUND := FALSE;
  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  SELECT invoiced INTO p_invoiced FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = p_invoiceid;

  IF NOT FOUND THEN
    RAISE EXCEPTION '#mne_lang#Rechnung wurde nicht verschickt#';
  END IF;
  
  IF p_invoiced <> true THEN
    RAISE EXCEPTION '#mne_lang#Rechnung wurde nicht verschickt#';
  END IF;
  
  FOUND := FALSE;
  SELECT COUNT(invoiceid) INTO i FROM mne_shipment.invoiceref WHERE invoiceid = p_invoiceid;
  
  IF FOUND AND i = 1 THEN
      SELECT invoicerefid INTO r_refid FROM mne_shipment.invoiceref WHERE invoiceid = p_invoiceid;
      PERFORM mne_shipment.invoiceref_payed(r_refid);
      return p_invoiceid;
  END IF;
  
  IF FOUND AND i > 1 THEN
      RAISE EXCEPTION '#mne_lang#Rechnung hat mehr als einen Empfänger - bitte einzeln quitieren#';
  END IF;
       
  UPDATE mne_shipment.invoiceref SET paid = true, modifydate = acttime, modifyuser = session_user
  WHERE  invoiceid = p_invoiceid;

  UPDATE mne_shipment.invoicemanagement SET paid = true, modifydate = acttime, modifyuser = session_user
  WHERE  invoicemanagementid = p_invoiceid;

  return p_invoiceid;
END;
 $$;


