CREATE FUNCTION mne_shipment.invoice_reminder1(p_invoiceid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    i INTEGER;
    p_invoiced BOOL;
    p_paid BOOL;
    p_paytime INTEGER;
    p_invoicetime INTEGER;
    p_reminder1 INTEGER;
    r RECORD;
    r_orderproductid VARCHAR;
BEGIN

  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  SELECT invoiced, paid, invoicetime, reminder1 INTO p_invoiced, p_paid, p_invoicetime, p_reminder1 FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = p_invoiceid;

  IF NOT FOUND THEN
    RAISE EXCEPTION '#mne_lang#Rechnung wurde nicht verschickt#';
  END IF;

  IF p_invoiced <> true THEN
    RAISE EXCEPTION '#mne_lang#Rechnung wurde nicht verschickt#';
  END IF;
  
  SELECT paytime INTO p_paytime FROM mne_shipment.invoice ti, mne_shipment.invoicecond tc WHERE ti.condid = tc.invoicecondid AND ti.invoiceid = p_invoiceid;
  
  IF p_paid = true OR ((((( p_invoicetime + p_paytime * 86400 )  - acttime) / 86400) + 1) >= 0 ) THEN
    RAISE EXCEPTION '#mne_lang#1. Mahnung ist noch nicht notwendig#';
  END IF;
  
  IF NOT p_reminder1 IS NULL THEN
      return p_invoiceid;
  END IF;
  
  UPDATE mne_shipment.invoicemanagement SET reminder1 = acttime, modifydate = acttime, modifyuser = session_user
  WHERE  invoicemanagementid = p_invoiceid;
  
  return p_invoiceid;
END;
 $$;


