CREATE FUNCTION mne_shipment.invoice_reminder2(p_invoiceid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    acttime INTEGER;
    i INTEGER;
    p_invoiced BOOL;
    p_paid BOOL;
    p_paytime INTEGER;
    p_invoicetime INTEGER;
    p_reminder1 INTEGER;
    p_reminder2 INTEGER;
    r RECORD;
    r_orderproductid VARCHAR;
    r_refcount INTEGER;
BEGIN

  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  
  SELECT invoiced,  paid,  invoicetime,  reminder1,   reminder2
  INTO p_invoiced,p_paid,p_invoicetime,p_reminder1, p_reminder2
  FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = p_invoiceid;

  IF NOT FOUND THEN
    RAISE EXCEPTION '#mne_lang#Rechnung wurde nicht verschickt#';
  END IF;

  IF p_invoiced <> true THEN
    RAISE EXCEPTION '#mne_lang#Rechnung wurde nicht verschickt#';
  END IF;

  IF p_reminder1 IS NULL THEN
    RAISE EXCEPTION '#mne_lang#1. Mahnung wurde nicht verschickt#';
  END IF;
  
  SELECT paytime INTO p_paytime FROM mne_shipment.invoice ti, mne_shipment.invoicecond tc WHERE ti.condid = tc.invoicecondid AND ti.invoiceid = p_invoiceid;
  
  IF p_paid = true OR ((((( p_invoicetime + ( p_paytime + 14 )* 86400 )  - acttime) / 86400) + 1) >= 0 ) THEN
    RAISE EXCEPTION '#mne_lang#2. Mahnung ist noch nicht notwendig#';
  END IF;

  SELECT DISTINCT t1.orderid, MAX(t1.deliverynoteid) as deliverynoteid, MAX(t2.position) + 1 AS position INTO r
  FROM mne_shipment.invoice t0 
    LEFT JOIN mne_shipment.deliverynote t1 ON ( t0.invoiceid = t1.invoiceid )
    LEFT JOIN mne_shipment.deliverynoteproduct t2 ON ( t1.deliverynoteid = t2.deliverynoteid )
  WHERE t0.invoiceid = p_invoiceid
  GROUP BY t1.orderid;
  
  SELECT COALESCE(NULLIF(count(*),0),1) INTO r_refcount FROM mne_shipment.invoiceref WHERE invoiceid = p_invoiceid;

  SELECT orderproductid INTO r_orderproductid FROM mne_crm.orderproduct WHERE orderid = r.orderid AND productid = 'reminder';
  IF NOT FOUND THEN
    SELECT INTO r_orderproductid mne_crm.orderproduct_add(r.orderid, '', 'reminder');
    DELETE FROM mne_personnal.orderproducttime WHERE orderproductid = r_orderproductid;
  END IF;
  
  UPDATE mne_crm.orderproduct SET actcount = r_refcount WHERE orderproductid = r_orderproductid;
  PERFORM deliverynoteid FROM mne_shipment.deliverynoteproduct WHERE deliverynoteid = r.deliverynoteid AND orderproductid = r_orderproductid;
  IF NOT FOUND THEN
  ELSE
    UPDATE mne_shipment.deliverynoteproduct SET count = r_refcount WHERE deliverynoteid = r.deliverynoteid AND orderproductid = r_orderproductid;
  END IF;

  IF NOT p_reminder2 IS NULL THEN
      return p_invoiceid;
  END IF;

  UPDATE mne_shipment.invoicemanagement SET pdf = '', reminder2 = acttime, modifydate = acttime, modifyuser = session_user
  WHERE  invoicemanagementid = p_invoiceid;

  return p_invoiceid;
END;
 $$;


