CREATE FUNCTION mne_shipment.invoiceref_ok(p_invoiceid character varying, p_refid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    v_invoiced BOOL;
    r_personid VARCHAR;
    r_companyid VARCHAR;
BEGIN

  SELECT INTO v_invoiced invoiced FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = p_invoiceid;

  IF v_invoiced = true THEN
    RAISE EXCEPTION '#mne_lang#Die Rechnung ist erfolgt und kann nicht geändert werden#';
  END IF;

  FOUND = FALSE;
  PERFORM invoicerefid FROM mne_shipment.invoiceref WHERE invoiceid = p_invoiceid AND refid = p_refid;

  IF FOUND = true THEN
    RAISE WARNING '#mne_lang#Kontakt ist schon hinzugefügt#';
    return 'ok';
  END IF;

  FOUND = FALSE;
  PERFORM personid FROM mne_crm.person WHERE personid = p_refid;

  IF FOUND = FALSE THEN
     PERFORM companyid FROM mne_crm.company WHERE companyid = p_refid;
     IF FOUND = TRUE THEN
       r_companyid = p_refid;
     END IF;
  ELSE
     r_personid = p_refid;
  END IF;

  IF FOUND = FALSE THEN
    RAISE EXCEPTION '#mne_lang#Kontakt ist unbekannt#';
  END IF;

  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  VALUES ( mne_catalog.mk_id(), p_invoiceid, p_refid, r_companyid, r_personid, false, session_user, acttime, session_user, acttime );

  return 'ok';
END;
 $$;


