CREATE FUNCTION mne_shipment.invoiceref_payed(p_invoicerefid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    r RECORD;
    i INTEGER;
    p_invoiced BOOL;
    p_invoiceid VARCHAR;
BEGIN

  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  FOUND := FALSE;
  SELECT invoiced, invoicemanagementid INTO p_invoiced, p_invoiceid  FROM mne_shipment.invoicemanagement 
  WHERE invoicemanagementid =
   ( select invoiceid from mne_shipment.invoiceref WHERE invoicerefid = p_invoicerefid );

  IF NOT FOUND THEN
    RAISE EXCEPTION '#mne_lang#Rechnung wurde nicht verschickt#';
  END IF;
  
  IF p_invoiced <> true THEN
    RAISE EXCEPTION '#mne_lang#Rechnung wurde nicht verschickt#';
  END IF;
  
  FOUND := FALSE;
  PERFORM invoicerefid FROM mne_shipment.invoiceref WHERE invoicerefid = p_invoicerefid AND paid <> true;
  IF NOT FOUND THEN
    RAISE WARNING '#mne_lang#Rechnung ist schon als bezahlt makiert#';
  END IF;

  SELECT DISTINCT INTO r
    t0."invoiceid" AS invoiceid,
    t4."withvat" AS withvat,
    t6."count" AS costumercount,
    COALESCE(t6.count,1.0) AS rescostumercount,
    SUM(t2.count * (t3.productprice * COALESCE(t5.rate_purchase,1.0))) / COALESCE(t6.count,1.0) AS netprice,
    SUM(t2.count * (t3.productprice * COALESCE(t5.rate_purchase,1.0))) AS fullnetprice,
    SUM(t4.withvat * (COALESCE(t3.productvat, (select vat from mne_crm.productdefault)))) AS resvat,
    SUM(t4.withvat * (COALESCE(t3.productvat, (select vat from mne_crm.productdefault))) / 100 * t2.count * (t3.productprice * COALESCE(t5.rate_purchase,1.0))) / COALESCE(t6.count,1.0) AS resabsvat,
    SUM((1 + t4.withvat * (COALESCE(t3.productvat, (select vat from mne_crm.productdefault))) / 100) * t2.count * (t3.productprice * COALESCE(t5.rate_purchase,1.0))) / COALESCE(t6.count,1.0) AS grossprice,
    t5."currency" AS currency
  FROM
    (((((((mne_shipment.invoice t0 LEFT JOIN mne_shipment.deliverynote t1 ON ( t0.invoiceid = t1.invoiceid) )
    LEFT JOIN mne_shipment.deliverynoteproduct t2 ON ( t1.deliverynoteid = t2.deliverynoteid) )
    LEFT JOIN mne_crm.orderproduct t3 ON ( t2.orderproductid = t3.orderproductid) )
    LEFT JOIN mne_crm.order t4 ON ( t3.orderid = t4.orderid) ))
    LEFT JOIN mne_base.currency t5 ON ( t3.productcurrencyid = t5.currencyid))
    LEFT JOIN mne_shipment.invoicerefcount t6 ON ( t0.invoiceid = t6.invoiceid))
  WHERE t0.invoiceid = p_invoiceid AND NOT t3.orderproductid like 'reminder%'
  GROUP BY t0.invoiceid, t4.withvat, t6.count, t5.currency, t5.rate_purchase;
  
  UPDATE mne_shipment.invoiceref SET paid = true, modifydate = acttime, modifyuser = session_user
  WHERE  invoicerefid = p_invoicerefid;

  PERFORM invoicerefid FROM mne_shipment.invoicepaid WHERE invoicerefid = p_invoicerefid;
  IF FOUND THEN
    UPDATE mne_shipment.invoicepaid SET paysum = r.grossprice, modifydate = acttime, modifyuser = session_user
    WHERE  invoicerefid = p_invoicerefid;
  ELSE
  END IF;
  
  FOUND := FALSE;
  SELECT COUNT(invoiceid) INTO i FROM mne_shipment.invoiceref WHERE invoiceid = p_invoiceid AND paid <> true;

  IF FOUND AND i > 0 THEN
    return 'ok';
  END IF;

  UPDATE mne_shipment.invoicemanagement SET paid = true, modifydate = acttime, modifyuser = session_user
  WHERE  invoicemanagementid = p_invoiceid;

  return 'ok';
END;
 $$;


