CREATE FUNCTION mne_shipment.invoicetime_update(p_invoicetimeid character varying, p_start character varying, p_duration character varying, p_invoicenum integer) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    acttime INTEGER;
    r_duration INT4;
    r_start INT4;
    r_invoiceid varchar;
    r RECORD;
    
BEGIN

  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  
  IF p_duration <> '' THEN
    SELECT CAST(p_duration AS INT4 ) INTO r_duration;
  END IF;
  
  IF p_start <> '' THEN
    SELECT CAST(p_start AS INT4 ) + mne_catalog.epoch_day(t0.start) INTO r_start 
    FROM mne_shipment.invoicetime t0 INNER JOIN mne_personnal.time t1 ON ( t0.timeid = t1.timeid AND t0.invoicetimeid = p_invoicetimeid );
  END IF;
  
  SELECT invoiceid INTO r_invoiceid FROM mne_shipment.invoicetime WHERE invoicetimeid = p_invoicetimeid;
  SELECT * INTO r FROM mne_shipment.invoice WHERE invoiceid = r_invoiceid;
      
  PERFORM invoiced FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = r_invoiceid AND invoiced = false;
  IF NOT FOUND THEN
     RAISE EXCEPTION '#mne_lang#Die Rechnung ist erfolgt und kann nicht geändert werden#';
  END IF;

  PERFORM invoiced FROM mne_shipment.invoicemanagement t0 INNER JOIN mne_shipment.invoice t1 ON ( t0.invoicemanagementid = t1.invoiceid ) WHERE t1.num = p_invoicenum AND t0.invoiced = false;
  IF NOT FOUND THEN
     RAISE EXCEPTION '#mne_lang#Die Rechnung ist erfolgt und kann nicht geändert werden#';
  END IF;
  
  IF r.num <> p_invoicenum THEN
    SELECT DISTINCT t3.invoiceid INTO r_invoiceid FROM mne_shipment.deliverynote t0
          INNER JOIN mne_shipment.deliverynote t1 ON (  t0.deliverynoteid != t1.deliverynoteid AND t0.orderid = t1.orderid )
          INNER JOIN mne_shipment.invoice t2 ON ( t0.invoiceid = t2.invoiceid )
          INNER JOIN mne_shipment.invoice t3 ON ( t1.invoiceid = t3.invoiceid AND t3.invoiceid <> t2.invoiceid )
    WHERE t2.num = r.num AND t3.num = p_invoicenum ;
    IF NOT FOUND THEN
      RAISE EXCEPTION '#mne_lang#Ausgewählte Rechnung gehöhrt nicht zum gleiche Auftrag#';
    END IF;
  END IF;
  
  UPDATE mne_shipment.invoicetime
     SET 
       duration = r_duration,
       start =  r_start,
       invoiceid = r_invoiceid
  WHERE
       invoicetimeid = p_invoicetimeid;             
  
  return p_invoicetimeid;
END $$;


