--
-- Name: invoicetransaction_refset(character varying, character varying); Type: FUNCTION; Schema: mne_shipment; Owner: admindb
--

CREATE FUNCTION mne_shipment.invoicetransaction_refset(p_umsatzid character varying, p_refid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    acttime INTEGER;
    r_invoicetransactionid VARCHAR;
    r RECORD;
BEGIN

  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  FOUND := FALSE;
  SELECT invoicetransactionid INTO r_invoicetransactionid FROM mne_shipment.invoicetransaction WHERE umsatzid = p_umsatzid;
  IF FOUND = TRUE THEN
     RAISE EXCEPTION '#mne_lang#Die Buchung wurde schon zugewiesen#';
  END IF;
  
  FOUND := FALSE;
  SELECT umsatzid INTO r_invoicetransactionid FROM mne_shipment.invoicetransaction WHERE refid = p_refid;
  IF FOUND = TRUE THEN
     FOUND = FALSE;
     SELECT * INTO r FROM mne_shipment.invoice WHERE invoiceid = p_refid;
     IF FOUND = FALSE THEN
        SELECT * INTO r FROM mne_shipment.invoiceref t0 LEFT JOIN mne_shipment.invoice t1 ON t0.invoiceid = t1.invoiceid WHERE t0.invoicerefid = p_refid;
     END IF;
     RAISE WARNING '#mne_lang#Die Rechnung hat schon eine Buchung#';
  END IF;
  
  FOUND := FALSE;
  SELECT * INTO r FROM mne_shipment.invoice WHERE invoiceid = p_refid;
  IF FOUND = FALSE THEN
    SELECT * INTO r FROM mne_shipment.invoiceref WHERE invoicerefid = p_refid;
    IF FOUND = FALSE THEN
      RAISE EXCEPTION '#mne_lang#Die Rechnung ist unbekannt#';
    END IF;
  END IF;
  
  SELECT * INTO r FROM mne_shipment.invoicemanagement WHERE invoicemanagementid = r.invoiceid;
  IF r.invoiced = FALSE THEN
      RAISE EXCEPTION '#mne_lang#Die Rechnung wurde nicht versendet#';
  END IF;
  
  SELECT mne_catalog.mk_id() INTO r_invoicetransactionid;
    
  return r_invoicetransactionid;
END;
 $$;


