CREATE FUNCTION mne_warehouse.offerproductpart_ok(p_offerproductid character varying, p_offerproductpartid character varying, p_partgroup character varying, p_partdescription character varying, p_partid character varying, p_fixturetypeid character varying, p_count double precision, p_partcost double precision, p_unit character varying, p_ownerid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    result varchar;
    acttime integer;
    pp_offerproductpartid varchar;
    pp_offerproductid varchar;
    r_count int4;
    r_cost float8;
BEGIN
    
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

   IF p_partid <> '' AND p_fixturetypeid <> '' THEN
       RAISE EXCEPTION '#mne_lang#Teil kann entweder ein Lagerteil oder Inventar sein#';
   END IF;
   
   r_cost = p_partcost;
   IF p_partid <> '' THEN
       SELECT unitcost INTO r_cost FROM mne_warehouse.partcost WHERE partid = p_partid;
       IF NOT FOUND THEN
         r_cost = p_partcost;
         RAISE WARNING '#mne_lang#Lagerteil hat keine hinterlegten Kosten#';
       END IF;
   END IF;

   IF p_fixturetypeid <> '' THEN
       SELECT unitcost INTO r_cost FROM mne_fixture.fixturetypecost WHERE fixturetypeid = p_fixturetypeid;
       IF NOT FOUND THEN
         r_cost = p_partcost;
         RAISE WARNING '#mne_lang#Inventar hat keine hinterlegten Kosten#';
       END IF;
   END IF;
   
   IF r_cost <> p_partcost THEN
       RAISE WARNING '#mne_lang#Kosten werden mit hinterlegten Kosten überschrieben#';
   END IF;
 
   IF p_offerproductpartid = '################' THEN

      SELECT COUNT(*) INTO r_count from mne_warehouse.offerproductpart WHERE offerproductid = p_offerproductid;
      IF r_count = 0 THEN
             createuser, createdate, modifyuser, modifydate )
          SELECT
            mne_catalog.mk_id(), p_offerproductid, t0.partgroup, t0.partdescription, t0.partid, t0.fixturetypeid, t0.count, t0.partcost,
            session_user, acttime, session_user, acttime
          FROM mne_warehouse.productpart t0 LEFT JOIN mne_crm.offerproduct t1 ON t0.productid = t1.productid
          WHERE t1.offerproductid = p_offerproductid AND t0.partdescription <> p_partdescription;
      END IF;

      SELECT mne_catalog.mk_id() INTO pp_offerproductpartid;
          createuser, createdate, modifyuser, modifydate )
      VALUES
        ( p_offerproductid, pp_offerproductpartid, p_partgroup, p_partdescription, NULLIF(p_partid,''), NULLIF(p_fixturetypeid,''), p_count, r_cost, p_unit, NULLIF(p_ownerid, ''),
          session_user, acttime, session_user, acttime );

   ELSE

     SELECT offerproductid INTO pp_offerproductid   
     FROM mne_warehouse.offerproductpart
     WHERE offerproductpartid = p_offerproductpartid;
     
     UPDATE mne_warehouse.offerproductpart SET
       offerproductid = p_offerproductid,
       partgroup = p_partgroup,
       partdescription = p_partdescription,
       count = p_count,
       partcost = r_cost,
       partid = NULLIF(p_partid,''),
       fixturetypeid = NULLIF(p_fixturetypeid, ''),
       unit = p_unit,
       ownerid = NULLIF(p_ownerid, ''),
       modifyuser = session_user,
       modifydate = acttime
     WHERE
       offerproductpartid = p_offerproductpartid;
     pp_offerproductpartid = p_offerproductpartid;

   END IF;
   
   UPDATE mne_crm.offerproduct SET productcost = mne_crm.offerproduct_cost(offerproductid)
   WHERE offerproductid = p_offerproductid AND productcostrecalc = true;
   
   return pp_offerproductpartid;
END $$;


