CREATE FUNCTION mne_warehouse.partcost_del(p_partcostid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    r_partid varchar;
    r_count integer;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    SELECT partid INTO r_partid FROM mne_warehouse.partcost WHERE partcostid = p_partcostid;

    SELECT COUNT(*) INTO r_count FROM mne_warehouse.productpart WHERE partid = r_partid;
    IF r_count <> 0 THEN
      RAISE EXCEPTION '#mne_lang#Die Kosten werden noch von einem Produkt benötigt#';
    END IF;
    
    SELECT COUNT(*) INTO r_count FROM mne_warehouse.offerproductpart WHERE partid = r_partid;
    IF r_count <> 0 THEN
      RAISE EXCEPTION '#mne_lang#Die Kosten werden noch von einem Angebot benötigt#';
    END IF;

    SELECT COUNT(*) INTO r_count FROM mne_warehouse.orderproductpart WHERE partid = r_partid;
    IF r_count <> 0 THEN
      RAISE EXCEPTION '#mne_lang#Die Kosten werden noch von einem Auftrag benötigt#';
    END IF;

    DELETE FROM mne_warehouse.partcost WHERE partcostid = p_partcostid;
    
    RETURN 'ok';
END

 $$;


