--
-- Name: partcost_ok(character varying, character varying, double precision, character varying); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.partcost_ok(p_partcostid character varying, p_partid character varying, p_unitcost double precision, p_unit character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE 
    acttime INTEGER;
    result varchar;
    r RECORD;
 BEGIN
    
    SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

    IF p_partcostid = '################' THEN
        SELECT INTO result mne_catalog.mk_id();
            createuser, createdate, modifyuser, modifydate )
        VALUES
          ( result, p_partid, p_unitcost, p_unit,
            session_user, acttime, session_user, acttime );
    ELSE
        result = p_partcostid;
        UPDATE mne_warehouse.partcost
        SET
          partid = p_partid,
          unitcost = p_unitcost,
          unit = p_unit,
          modifyuser = session_user,
          modifydate = acttime
        WHERE
          partcostid = result;
    END IF;

    UPDATE mne_warehouse.productpart SET partcost = p_unitcost WHERE partid = p_partid;
    UPDATE mne_warehouse.offerproductpart SET partcost = p_unitcost WHERE partid = p_partid;
    UPDATE mne_warehouse.orderproductpart SET partcost = p_unitcost WHERE partid = p_partid;

    FOR r IN SELECT DISTINCT productid from mne_warehouse.productpart WHERE partid = p_partid LOOP
      UPDATE mne_crm.productprice SET unitcost = mne_crm.productcost(r.productid), modifyuser = session_user, modifydate = acttime WHERE productid = r.productid;
      PERFORM mne_crm.productcost_update(r.productid, mne_crm.productcost(r.productid));
    END LOOP;

    FOR r IN SELECT DISTINCT offerproductid from mne_warehouse.offerproductpart WHERE partid = p_partid LOOP
      UPDATE mne_crm.offerproduct SET productcost = mne_crm.offerproduct_cost(r.offerproductid)
      WHERE offerproductid = r.offerproductid AND productcostrecalc = true;
    END LOOP;

    FOR r IN SELECT DISTINCT orderproductid from mne_warehouse.orderproductpart WHERE partid = p_partid LOOP
      UPDATE mne_crm.orderproduct SET productcost = mne_crm.orderproductcost(r.orderproductid)
      WHERE orderproductid = r.orderproductid AND productcostrecalc = true;
    END LOOP;
    
    RETURN result;
END

 $$;


