CREATE FUNCTION mne_warehouse.partingoing_ok(p_partingoingid character varying, p_purchasedeliveryid character varying, p_partstoragelocationid character varying, p_storagelocationid character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE
    result varchar;
    acttime integer;
    r_count integer;
    r_partid varchar;
    r_partstoragelocationid varchar;
    l_partid varchar;
BEGIN
    
  SELECT INTO acttime  CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  IF p_partingoingid = '################' THEN
    
    IF p_storagelocationid = '' THEN
        RAISE EXCEPTION '#mne_lang#Bitte einen Lagerplatz auswählen#';
    END IF;
    
    SELECT mne_catalog.mk_id() INTO result;
    SELECT mne_catalog.mk_id() INTO r_partstoragelocationid;
    
    SELECT t1.partid, t1.count * COALESCE(t3.packagesize,1) INTO r_partid, r_count 
      FROM mne_warehouse.purchasedelivery t0
        LEFT JOIN mne_warehouse.purchase t1 ON ( t0.purchaseid = t1.purchaseid )
        LEFT JOIN mne_warehouse.part t2 ON t2.partid = t1.partid
        LEFT JOIN mne_warehouse.partvendor t3 ON t3.partid = t2.partid AND t3.partvendorid = t1.partvendorid
      WHERE t0.purchasedeliveryid = p_purchasedeliveryid ;




    UPDATE mne_warehouse.partingoing SET partstoragelocationid = r_partstoragelocationid WHERE partingoingid = result;

  ELSE
    result = p_partingoingid;
    
    FOUND = FALSE;
    PERFORM partstoragelocationid 
      FROM mne_warehouse.partingoing t0 
      WHERE t0.partingoingid = p_partingoingid AND t0.partstoragelocationid ISNULL;
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Ware ist schon eingelagert#';
    END IF;

    FOUND = FALSE;
    PERFORM partingoingid
      FROM mne_warehouse.partingoing t0 
      WHERE t0.partingoingid = p_partingoingid AND t0.partstoragelocationid = p_partstoragelocationid;
    IF NOT FOUND THEN
      RAISE EXCEPTION '#mne_lang#Lagerplatz gehöhrt nicht zur Einlagerung#';
    END IF;

    FOUND = FALSE;
    PERFORM t0.partstoragelocationid 
      FROM mne_warehouse.partstoragelocation t0 
        INNER JOIN mne_warehouse.partingoing t1 ON ( t0.partstoragelocationid <> t1.partstoragelocationid AND t0.partingoingid = t1.partingoingid )
      WHERE t0.partingoingid = p_partingoingid;
    IF FOUND THEN
      RAISE WARNING '%', p_partingoingid;
      RAISE EXCEPTION '#mne_lang#Waren sind schon teilweise oder gesamt eingelagert#';
    END IF;

    FOUND = FALSE;
    PERFORM t0.relocationid
      FROM mne_warehouse.relocation t0 
        LEFT JOIN  mne_warehouse.partingoing t1 ON ( t0.partstoragelocationid = t1.partstoragelocationid )
      WHERE t0.partingoingid = p_partingoingid;      
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Für die Waren existieren Umlageraufträge#';
    END IF;

    FOUND = FALSE;
    PERFORM partoutgoingid 
      FROM mne_warehouse.partoutgoing t0 
        LEFT JOIN  mne_warehouse.partingoing t1 ON ( t0.partingoingid = t1.partingoingid )
      WHERE t0.partingoingid = p_partingoingid;      
    IF FOUND THEN
      RAISE EXCEPTION '#mne_lang#Ware ist schon teilweise oder gesamt ausgelagert#';
    END IF;

    SELECT t1.partid, t1.count * COALESCE(t3.packagesize,1) INTO r_partid, r_count 
      FROM mne_warehouse.purchasedelivery t0
        LEFT JOIN mne_warehouse.purchase t1 ON ( t0.purchaseid = t1.purchaseid )
        LEFT JOIN mne_warehouse.part t2 ON t2.partid = t1.partid
        LEFT JOIN mne_warehouse.partvendor t3 ON t3.partid = t2.partid AND t3.partvendorid = t1.partvendorid
      WHERE t0.purchasedeliveryid = p_purchasedeliveryid ;


    UPDATE mne_warehouse.partstoragelocation
      SET storagelocationid = p_storagelocationid,
          partid =  r_partid,
          count = r_count,
          modifyuser = session_user,
          modifydate = acttime
    WHERE partstoragelocationid = p_partstoragelocationid;
    
    UPDATE mne_warehouse.partingoing
      SET purchasedeliveryid = p_purchasedeliveryid,
          modifyuser = session_user,
          modifydate = acttime
     WHERE partingoingid = p_partingoingid;       
  END IF;   
    
 return result;
END; $$;


