CREATE FUNCTION mne_warehouse.partoutgoing_fetch(p_partoutgoingid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    r_partstoragelocationid varchar;
    r_spartstoragelocationid varchar;
    r_relocationid varchar;
    r_orderproductpartid varchar;
    r_partid varchar;
    r_count float8;
    acttime integer;
BEGIN
    
  SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  SELECT t0.partstoragelocationid INTO r_partstoragelocationid FROM mne_warehouse.partoutgoing t0 WHERE t0.partoutgoingid = p_partoutgoingid;
  
  SELECT t0.partstoragelocationid, t0.spartstoragelocationid, t0.relocationid, t0.orderproductpartid, t0.count, t3.partid
    INTO  r_partstoragelocationid, r_spartstoragelocationid, r_relocationid, r_orderproductpartid, r_count, r_partid
    FROM mne_warehouse.partoutgoing t0
      LEFT JOIN mne_warehouse.partingoing t1 ON ( t0.partingoingid = t1.partingoingid )
      LEFT JOIN mne_warehouse.purchasedelivery t2 ON ( t1.purchasedeliveryid = t2.purchasedeliveryid )
      LEFT JOIN mne_warehouse.purchase t3 ON ( t2.purchaseid = t3.purchaseid )
    WHERE t0.partoutgoingid = p_partoutgoingid;

   IF NOT r_relocationid IS NULL THEN
       RAISE EXCEPTION '#mne_lang#Lieferung ist noch nicht ausgelagert#';
   END IF;    

   UPDATE mne_warehouse.orderproductpart
     SET actcount = COALESCE(( SELECT SUM(count) FROM mne_warehouse.partoutgoing
                    WHERE orderproductpartid = r_orderproductpartid
                    GROUP BY orderproductpartid ),0),
         modifyuser = session_user,
         modifydate = acttime
   WHERE orderproductpartid = r_orderproductpartid;
   
   UPDATE mne_warehouse.partoutgoing SET partstoragelocationid = NULL, modifyuser = session_user, modifydate = acttime  WHERE partoutgoingid = p_partoutgoingid;
   DELETE FROM mne_warehouse.partstoragelocation WHERE partstoragelocationid = r_partstoragelocationid;

  PERFORM partid FROM mne_warehouse.storageoptpart WHERE partid = r_partid;
  IF FOUND THEN
    UPDATE mne_warehouse.storageoptpart SET count = count + r_count WHERE partid = r_partid;
  ELSE
  END IF;
   
   return p_partoutgoingid;
