--
-- Name: partoutgoing_ok(character varying, character varying, character varying, character varying, integer, character varying); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.partoutgoing_ok(p_partoutgoingid character varying, p_storagelocationid character varying, p_spartstoragelocationid character varying, p_orderproductpartid character varying, p_count integer, p_personid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    result varchar;
    r_relocationid varchar;
    r_partstoragelocationid varchar;
    r_createdate int4;
    r_createuser varchar;
    r_sequence int4;
    r_count int4;
    acttime int4;
    r RECORD;
BEGIN
    
  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  IF p_partoutgoingid <> '################' THEN
    SELECT relocationid, partstoragelocationid, createdate, createuser
    INTO r_relocationid, r_partstoragelocationid, r_createdate, r_createuser
    FROM mne_warehouse.partoutgoing
    WHERE partoutgoingid = p_partoutgoingid;

    IF r_relocationid IS NULL THEN
      RAISE EXCEPTION '#mne_lang#Teile sind schon ausgelagert#';
    END IF;
  END IF;

  IF p_orderproductpartid <> '' THEN
    PERFORM t0.partid
    FROM mne_warehouse.orderproductpart t0, mne_warehouse.partstoragelocation t1
    WHERE t0.partid = t1.partid AND t1.partstoragelocationid = p_spartstoragelocationid;
    
    IF NOT FOUND THEN
      RAISE EXCEPTION '#mne_lang#Lagerplatz enthält keine passende Teile#';
    END IF; 
    
    FOUND := FALSE;
    PERFORM DISTINCT t0."partstoragelocationid" AS partstoragelocationid,
       t0.count + COALESCE(t8.count,0) - COALESCE(t10.count,0) AS rescount
    FROM
      (((((((((((mne_warehouse.partstoragelocation t0 LEFT JOIN mne_warehouse.partingoing t1 ON ( t0.partingoingid = t1.partingoingid) )
        LEFT JOIN mne_warehouse.purchasedelivery t2 ON ( t1.purchasedeliveryid = t2.purchasedeliveryid) )
        LEFT JOIN mne_warehouse.purchase t3 ON ( t2.purchaseid = t3.purchaseid) )
        LEFT JOIN mne_crm.order t4 ON ( t3.crmorderid = t4.orderid) )
        LEFT JOIN mne_crm.orderproduct t5 ON ( t4.orderid = t5.orderid) )
        LEFT JOIN mne_warehouse.orderproductpart t6 ON ( t5.orderproductid = t6.orderproductid) ))
        LEFT JOIN mne_warehouse.partoutgoing t7 ON ( t0.partstoragelocationid = t7.partstoragelocationid) LEFT JOIN mne_warehouse.relocation t8 ON ( t7.relocationid = t8.relocationid) ))
        LEFT JOIN mne_warehouse.partoutgoing t9 ON ( t0.partstoragelocationid = t9.spartstoragelocationid) LEFT JOIN mne_warehouse.relocation t10 ON ( t9.relocationid = t10.relocationid) ))
     WHERE
       t6.partid = t0.partid
       AND t7.partoutgoingid isnull
       AND ( ( t0.count + COALESCE(t8.count,0) - COALESCE(t10.count,0) ) > 0 )
       AND t6.orderproductpartid = p_orderproductpartid
       AND t0.partstoragelocationid <> p_spartstoragelocationid;
      
      IF FOUND THEN
        RAISE EXCEPTION '#mne_lang#Es existiert ein Lagerplatz mit reservierten Teilen für diesen Auftrag#';
      END IF;
       
    ELSE
      PERFORM DISTINCT partoutgoingid FROM mne_warehouse.partoutgoing WHERE partoutgoingid = p_partoutgoingid AND orderproductpartid IS NOT NULL;
      IF FOUND THEN
        RAISE WARNING '#mne_lang#Reservierung für einen Auftrag wurde aufgehoben#';
      END IF;
    END IF;
  
  select p_count - COALESCE(SUM(count),0) INTO r_count from mne_warehouse.relocation WHERE partstoragelocationid = p_spartstoragelocationid;
  
  IF NOT mne_warehouse.partcheck_count(p_spartstoragelocationid, r_count ) THEN
    RAISE EXCEPTION '#mne_lang#Lagerplatz enthält zu wenige Teile#';
  END IF; 
  
  FOUND = false;
  PERFORM partstoragelocationid FROM mne_warehouse.partoutgoing WHERE partstoragelocationid = p_spartstoragelocationid;
  IF FOUND THEN
    RAISE EXCEPTION '#mne_lang# Lagerplatz gehöhrt zu einer Auslagerung#';
  END IF;
  
  IF p_partoutgoingid = '################' THEN
    SELECT mne_catalog.mk_id() INTO result;
    SELECT mne_catalog.mk_id() INTO r_relocationid;
    SELECT mne_catalog.mk_id() INTO r_partstoragelocationid;
    r_createdate = acttime;
    r_createuser = session_user;
  ELSE
    result = p_partoutgoingid;

    SELECT relocationid, partstoragelocationid, createdate, createuser
    INTO r_relocationid, r_partstoragelocationid, r_createdate, r_createuser
    FROM mne_warehouse.partoutgoing
    WHERE partoutgoingid = result;

    IF r_relocationid IS NULL THEN
      RAISE EXCEPTION '#mne_lang#Teile sind schon ausgelagert#';
    END IF;
    
    FOUND := FALSE;
    PERFORM DISTINCT t0."partoutgoingid" AS partoutgoingid
    FROM
      (((((((mne_warehouse.partoutgoing t0 LEFT JOIN mne_warehouse.orderproductpart t1 ON ( t0.orderproductpartid = t1.orderproductpartid) )
        LEFT JOIN mne_crm.orderproduct t2 ON ( t1.orderproductid = t2.orderproductid) )
        LEFT JOIN mne_crm.order t3 ON ( t2.orderid = t3.orderid) ))
        LEFT JOIN mne_warehouse.partingoing t4 ON ( t0.partingoingid = t4.partingoingid) LEFT JOIN mne_warehouse.purchasedelivery t5 ON ( t4.purchasedeliveryid = t5.purchasedeliveryid) )
        LEFT JOIN mne_warehouse.purchase t6 ON ( t5.purchaseid = t6.purchaseid) ))
    WHERE
      t3.orderid = t6.crmorderid AND t0.partoutgoingid = result AND p_orderproductpartid <> t0.orderproductpartid;
      
    IF FOUND = TRUE THEN
      RAISE EXCEPTION '#mne_lang#Die Teile können keinem anderen Auftrag zugeordnet werden#';
    END IF;
 
    UPDATE mne_warehouse.partoutgoing
    SET relocationid = NULL, partstoragelocationid = NULL
    WHERE partoutgoingid = result;

    DELETE FROM mne_warehouse.relocation WHERE relocationid = r_relocationid;
    DELETE FROM mne_warehouse.partstoragelocation WHERE partstoragelocationid = r_partstoragelocationid;
    DELETE FROM mne_warehouse.partoutgoing WHERE partoutgoingid = result;
  END IF;

      partid, partingoingid, count,
      createdate, createuser, modifydate, modifyuser )
    SELECT
      r_partstoragelocationid, p_storagelocationid,
      partid, partingoingid, 0,
      r_createdate, r_createuser, acttime, session_user
    FROM mne_warehouse.partstoragelocation
    WHERE partstoragelocationid = p_spartstoragelocationid;

  FOUND := false;
  PERFORM storagelocationid
    FROM mne_warehouse.partstoragelocation
    WHERE storagelocationid = p_storagelocationid AND partstoragelocationid = p_spartstoragelocationid;
  
  IF FOUND = TRUE THEN
    UPDATE mne_warehouse.partstoragelocation SET count = p_count WHERE partstoragelocationid = r_partstoragelocationid;
    UPDATE mne_warehouse.partstoragelocation SET count = count - p_count WHERE partstoragelocationid = p_spartstoragelocationid;
    DELETE FROM mne_warehouse.partstoragelocation WHERE count = 0 AND partstoragelocationid = p_spartstoragelocationid;
    r_relocationid = NULL;
  ELSE
    SELECT MAX(sequence) INTO r_sequence FROM mne_warehouse.relocation where partstoragelocationid = p_spartstoragelocationid;
    r_sequence = COALESCE(r_sequence,0);
    
        partingoingid, sequence, count, personid,
        createdate, createuser, modifydate, modifyuser )
      SELECT
        r_relocationid, p_spartstoragelocationid, p_storagelocationid,
        partingoingid, r_sequence, p_count, NULLIF(p_personid,''),
        r_createdate, r_createuser, acttime, session_user
      FROM mne_warehouse.partstoragelocation
        WHERE partstoragelocationid = p_spartstoragelocationid;
      
  END IF;
  
      spartstoragelocationid, partstoragelocationid, relocationid,
      orderproductpartid, count,
      createdate, createuser, modifydate, modifyuser )
    SELECT
      result, partingoingid,
      p_spartstoragelocationid, r_partstoragelocationid, r_relocationid,
      NULLIF(p_orderproductpartid,''), p_count,
      r_createdate, r_createuser, acttime, session_user
    FROM mne_warehouse.partstoragelocation
    WHERE partstoragelocationid = p_spartstoragelocationid;

  return result;
END $$;


