--
-- Name: parttree_needpurchase(character varying); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.parttree_needpurchase(p_treeid character varying) RETURNS boolean
    LANGUAGE plpgsql
    AS $$ DECLARE
    r RECORD;
BEGIN
  FOUND = FALSE;
  FOR r IN SELECT t0.parentid, t0.treeid, t0.partid, ( COALESCE(t2.sum, 0 )  - COALESCE(t4.sum, 0) < COALESCE(t1.mincount,0) ) AS needorder FROM mne_warehouse.parttree t0 
              LEFT JOIN mne_warehouse.partstoragemasterdata t1 ON t0.partid = t1.partid
              LEFT JOIN (SELECT t0.partid, COALESCE(sum(t1.count), 0::bigint) AS sum
                         FROM mne_warehouse.part t0
                         LEFT JOIN mne_warehouse.partstoragelocation t1 ON t0.partid::text = t1.partid::text
                         LEFT JOIN mne_warehouse.partingoing t2 ON t2.partingoingid = t1.partingoingid
                         LEFT JOIN mne_warehouse.purchasedelivery t3 ON t2.purchasedeliveryid = t3.purchasedeliveryid AND t3.purchaseid IS NULL
                         GROUP BY t0.partid ) t2 ON t0.partid = t2.partid
              LEFT JOIN mne_warehouse.partoutgoing_sum t4 ON t0.partid = t4.partid
           WHERE parentid = p_treeid OR ( treeid  = p_treeid AND t0.partid IS NOT NULL )
    LOOP
    
    IF r.partid IS NULL THEN
       IF mne_warehouse.parttree_needpurchase(r.treeid) = TRUE THEN
           return TRUE;
       END IF;
    ELSIF r.needorder = TRUE THEN
       return TRUE;
    END IF;
    
  END LOOP;
  
  return FALSE;  
END $$;


