CREATE FUNCTION mne_warehouse.productpart_ok(p_productpartid character varying, p_productid character varying, p_count double precision, p_partgroup character varying, p_partdescription character varying, p_partcost double precision, p_partid character varying, p_fixturetypeid character varying, p_unit character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    result varchar;
    acttime integer;
    r_cost float8;
    
BEGIN
  
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

   IF p_partid <> '' AND p_fixturetypeid <> '' THEN
       RAISE EXCEPTION '#mne_lang#Teil kann entweder ein Lagerteil oder Inventar sein#';
   END IF;
   
   r_cost = p_partcost;
   IF p_partid <> '' THEN
       SELECT unitcost INTO r_cost FROM mne_warehouse.partcost WHERE partid = p_partid;
       IF NOT FOUND THEN
         r_cost = p_partcost;
         RAISE WARNING '#mne_lang#Lagerteil hat keine hinterlegten Kosten#';
       END IF;
   END IF;

   IF p_fixturetypeid <> '' THEN
       SELECT unitcost INTO r_cost FROM mne_fixture.fixturetypecost WHERE fixturetypeid = p_fixturetypeid;
       IF NOT FOUND THEN
         r_cost = p_partcost;
         RAISE WARNING '#mne_lang#Inventar hat keine hinterlegten Kosten#';
       END IF;
   END IF;
   
   IF r_cost <> p_partcost THEN
       RAISE WARNING '#mne_lang#Kosten werden mit hinterlegten Kosten überschrieben#';
   END IF;
   
   IF p_productpartid = '################' THEN

     SELECT mne_catalog.mk_id() INTO result;
     
         createuser, createdate, modifyuser, modifydate )
     VALUES
       ( result, p_productid, p_count, p_partgroup, p_partdescription, r_cost, NULLIF(p_partid, ''), NULLIF(p_fixturetypeid,''), NULLIF(p_unit,''),
         session_user, acttime, session_user, acttime );

   ELSE

     result = p_productpartid;
     
     UPDATE mne_warehouse.productpart SET
       productid = p_productid ,
       partgroup = p_partgroup,
       partdescription = p_partdescription,
       count = p_count,
       partcost = r_cost, 
       partid = NULLIF(p_partid, ''),
       fixturetypeid =  NULLIF(p_fixturetypeid,''),
       unit =  NULLIF(p_unit,''),
       modifyuser = session_user,
       modifydate = acttime
     WHERE
       productpartid = p_productpartid;

   END IF;
   
   UPDATE mne_crm.productprice SET unitcost = mne_crm.productcost(p_productid), modifyuser = session_user, modifydate = acttime WHERE productid = p_productid;
   PERFORM mne_crm.productcost_update(p_productid, mne_crm.productcost(p_productid));

   return result;
END $$;


