--

CREATE FUNCTION mne_warehouse.purchase_mknumber(p_auto boolean) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE
  result varchar;
  r_num  int4;
  r_func varchar;
  acttime varchar;
BEGIN
    
  SELECT INTO r_func 'SELECT ' || funcschema || '.' || func || '(' || quote_literal(p_auto) || ')'
  FROM mne_application.customerfunctions WHERE customerfunction = 'warehouse_mknumber';
  IF FOUND THEN
    EXECUTE  r_func INTO result;
    return result;
  END IF;

  SELECT INTO acttime to_char(current_timestamp, 'YYYYMMDD');

  FOUND := TRUE;
  r_num = 1;
  WHILE FOUND = TRUE
  LOOP
    result = acttime || '-' || CAST ( r_num AS VARCHAR ); 
    PERFORM purchaseid FROM mne_warehouse.purchase WHERE ordernumber = result OR ordernumber = ( 'A' || result );
    IF FOUND = FALSE THEN
      IF p_auto THEN
        result = 'A' || result;
      END IF;
      return result;
    END IF;
    r_num = r_num + 1;
  END LOOP;

  return '';

END $$;


