    r_purchase mne_warehouse.purchase%rowtype;
BEGIN
    
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

   SELECT * INTO r_vendor FROM mne_warehouse.partvendor WHERE partvendorid = p_partvendorid AND partid = p_partid;
   IF NOT FOUND THEN
     RAISE EXCEPTION '%', '#mne_lang#Der Lieferant liefert dieses Teil nicht#';
