         session_user, acttime, session_user, acttime );

   ELSE

     FOUND := false;
     
     SELECT * INTO r_purchase FROM mne_warehouse.purchase WHERE purchaseid = p_purchaseid;

     IF NOT FOUND THEN
       RAISE EXCEPTION '#mne_lang#Bestellung nicht gefunden#';
     END IF;
     
     result = r_purchase.purchaseid;
     IF r_purchase.ordernumber IS NOT NULL AND substring(r_purchase.ordernumber FROM 1 FOR 1 ) <> 'A' THEN
