     UPDATE mne_warehouse.purchase SET
       partid = p_partid,
       partvendorid = p_partvendorid,
       vendorordernumber = r_vendor.ordernumber,
       count = p_count,
       expected = COALESCE(NULLIF(p_expected, 0), r_purchase.expected),
       ownerid = NULLIF(p_ownerid, ''), 
       crmorderid = NULLIF(p_crmorderid, ''),
