-- Name: purchase_send(character varying, boolean); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.purchase_send(p_purchaseid character varying, p_single boolean) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
  acttime integer;

  r_vendor   mne_warehouse.partvendor%rowtype;
  r_purchase mne_warehouse.purchase%rowtype;
  r_company  mne_crm.company%rowtype;
  
  r_ordernumber varchar;
  r_expected int4;
  
BEGIN
    
  SELECT INTO acttime
    CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  FOUND := false;
  SELECT * INTO r_purchase FROM mne_warehouse.purchase WHERE purchaseid = p_purchaseid;

  IF NOT FOUND THEN
    RAISE EXCEPTION '#mne_lang#Bestellung nicht gefunden#';
  END IF;

  IF r_purchase.ordernumber IS NOT NULL THEN
    RAISE WARNING '#mne_lang#Bestellung ist schon erfolgt#';
    return p_purchaseid;
  END IF;
     
  SELECT * INTO r_vendor FROM mne_warehouse.partvendor WHERE partvendorid = r_purchase.partvendorid;

  r_ordernumber = mne_warehouse.purchase_mknumber(false);
  r_expected = (r_vendor.leadtime * 84600) + acttime;
  
  IF p_single = true THEN
    UPDATE mne_warehouse.purchase SET
      orderdate = acttime,
      expected = r_expected,
      ordernumber = r_ordernumber,
      modifyuser = session_user,
      modifydate = acttime
    WHERE
      purchaseid = p_purchaseid;
  ELSE

    IF r_purchase.partvendorid IS NULL THEN
      RAISE EXCEPTION '#mne_lang#Bestellungen ohne Lieferanten können nur einzeln bestellt werden#';
    END IF;
    
    SELECT * INTO r_company FROM mne_crm.company WHERE companyid = ( SELECT companyid FROM mne_warehouse.partvendor WHERE partvendorid = r_purchase.partvendorid);
    UPDATE mne_warehouse.purchase SET
      orderdate = acttime,
      expected = r_expected,
      ordernumber = r_ordernumber,
      modifyuser = session_user,
      modifydate = acttime
    WHERE
      purchaseid in ( SELECT purchaseid FROM mne_warehouse.purchase t0
                                               LEFT JOIN mne_warehouse.partvendor t1 ON t0.partvendorid = t1.partvendorid
                                               LEFT JOIN mne_crm.company t2 on t1.companyid = t2.companyid
                                          WHERE t2.companyid = r_company.companyid AND COALESCE(t0.ordernumber,'') = '' );
  END IF;
  
  return p_purchaseid;
END $$;


