CREATE FUNCTION mne_warehouse.purchasedelivery_del(p_purchasedeliveryid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    acttime integer;
    r RECORD;
BEGIN
    
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

