   FROM
     mne_warehouse.purchasedelivery t0
       LEFT JOIN mne_warehouse.purchasedelivery_sum t1 ON ( t0.purchaseid = t1.purchaseid )
       LEFT JOIN mne_warehouse.purchase t2 ON ( t1.purchaseid = t2.purchaseid )
  WHERE t0.purchasedeliveryid = p_purchasedeliveryid;

  DELETE FROM mne_warehouse.purchasedelivery WHERE purchasedeliveryid = p_purchasedeliveryid;
  IF r.auto THEN
    DELETE FROM mne_warehouse.purchase WHERE purchaseid = r.purchaseid;
  ELSE
    IF r.count < r.sum THEN
      RAISE WARNING '#mne_lang#Gelieferte Menge ist grösser als bestellte Menge#';
    END IF;
   
    IF r.count > r.sum THEN
      UPDATE
        mne_warehouse.purchase
      SET
        deliverydate = NULL,
        modifyuser = session_user,
        modifydate = acttime
      WHERE
        purchaseid = r.purchaseid;
    END IF;
  END IF;

  return 'ok';
END $$;


ALTER FUNCTION mne_warehouse.purchasedelivery_del(p_purchasedeliveryid character varying) OWNER TO admindb;

--
-- Name: purchasedelivery_ok(character varying, character varying, character varying, character varying, integer, integer, character varying, boolean, character varying, character varying, character varying, character varying); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.purchasedelivery_ok(p_purchasedeliveryid character varying, p_purchaseid character varying, p_partid character varying, p_deliverynotenumber character varying, p_deliverydate integer, p_count integer, p_documentnumber character varying, p_overwrite boolean, p_partingoingid character varying, p_partstoragelocationid character varying, p_storagelocationid character varying, p_partvendorid character varying) RETURNS character varying
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$ DECLARE
    result varchar;
    acttime integer;

    r_deliverysum mne_warehouse.purchasedelivery_sum%rowtype;
    r_purchase mne_warehouse.purchase%rowtype;
    r_documentnumber varchar;
    r_partingoingid varchar;
    r_purchaseid varchar;
    r_ordernumber varchar;
    r_ownerid varchar;
    
BEGIN
    
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

   FOUND := false;
   SELECT * INTO r_purchase FROM mne_warehouse.purchase WHERE purchaseid = p_purchaseid;
   IF NOT FOUND THEN
     IF p_partid = '' THEN
       RAISE EXCEPTION '#mne_lang#Zur Lieferung gibt es keine Bestellung#';
     ELSE
       RAISE WARNING '#mne_lang#Zur Lieferung gibt es keine Bestellung - erstelle eine#';
     END IF;
   END IF;
   
   IF FOUND AND NULLIF(r_purchase.ordernumber, '') IS NULL THEN
       RAISE EXCEPTION '#mne_lang#Bestellung ist noch nicht versendet worden#';
   END IF;
   
   IF FOUND AND p_deliverynotenumber = '' THEN
     RAISE EXCEPTION '#mne_lang#Bitte eine Lieferscheinnummer angeben#';
   END IF;
   
   IF NOT r_purchase.deliverydate IS NULL THEN
     IF p_overwrite = true THEN
       RAISE WARNING '#mne_lang#Bestellung wurde schon komplett geliefert#';
     ELSE
       RAISE EXCEPTION '#mne_lang#Bestellung wurde schon komplett geliefert#';
     END IF;
   END IF;

   IF p_purchasedeliveryid = '################' THEN

     SELECT mne_catalog.mk_id() INTO result;
     IF  p_documentnumber = '' THEN
         FOUND := false;
         SELECT DISTINCT COALESCE(documentnumber,'') INTO r_documentnumber FROM mne_warehouse.purchasedelivery WHERE deliverynotenumber = p_deliverynotenumber AND purchaseid = p_purchaseid;

         IF FOUND = false OR p_deliverynotenumber = '' THEN
           SELECT to_char(MAX(CAST(COALESCE(NULLIF(documentnumber,''),'1') AS int8) +1 ), 'FM99999999999999999999') INTO r_documentnumber
           FROM mne_warehouse.purchasedelivery; 
           IF r_documentnumber IS NULL THEN
               r_documentnumber = '1';
           END IF;
         ELSE
           RAISE EXCEPTION '#mne_lang#Der Lieferschein dieser Bestellung wurde schon erfasst#';
         END IF;
     ELSE
         r_documentnumber = p_documentnumber;
     END IF;
     
     r_purchaseid = r_purchase.purchaseid;
     r_ordernumber = '';
     IF r_purchaseid IS NULL THEN
       SELECT mne_warehouse.purchase_mknumber(true) INTO r_ordernumber;
       SELECT mne_catalog.mk_id() INTO r_purchaseid;
       SELECT personid INTO r_ownerid FROM mne_crm.personowndata WHERE loginname = session_user;
       
           createuser, createdate, modifyuser, modifydate )
       VALUES
         ( r_purchaseid, p_count, acttime, r_ordernumber, acttime, acttime, p_partid, NULLIF(p_partvendorid, ''), true, r_ownerid, 
           session_user, acttime, session_user, acttime );
     END IF;
     
         createuser, createdate, modifyuser, modifydate )
     VALUES
       ( result, NULLIF(r_purchaseid,''), COALESCE(NULLIF(p_deliverynotenumber,''), r_ordernumber), COALESCE(NULLIF(p_deliverydate,0),acttime) , p_count, r_documentnumber,
         session_user, acttime, session_user, acttime );

   ELSE

     result = p_purchasedeliveryid;
     
     FOUND := false;
     PERFORM * FROM mne_warehouse.purchasedelivery WHERE purchasedeliveryid = result AND purchaseid = p_purchaseid;
     IF FOUND = false THEN
        RAISE EXCEPTION '#mne_lang#Das gelieferte Teil darf nicht verändert werden - bitte löschen und neu anlegen#';
     END IF;
 
     FOUND := false;
     SELECT DISTINCT documentnumber INTO r_documentnumber FROM mne_warehouse.purchasedelivery WHERE deliverynotenumber = p_deliverynotenumber AND p_deliverynotenumber  != '';
     IF r_documentnumber <> p_documentnumber THEN
       UPDATE mne_warehouse.purchasedelivery SET documentnumber = p_documentnumber WHERE documentnumber = r_documentnumber;
     END IF;
     
    UPDATE mne_warehouse.purchasedelivery SET
       purchaseid         = NULLIF(p_purchaseid,''),
       deliverynotenumber = COALESCE(NULLIF(p_deliverynotenumber,''),'----'),
       deliverydate       = COALESCE(NULLIF(p_deliverydate,0),acttime),
       count              = p_count,
       modifyuser         = session_user,
       modifydate         = acttime
     WHERE
       purchasedeliveryid = p_purchasedeliveryid;

   END IF;
   
   SELECT * INTO r_deliverysum FROM mne_warehouse.purchasedelivery_sum WHERE purchaseid = p_purchaseid;

   IF r_purchase.count < r_deliverysum.sum THEN
     IF p_overwrite = true THEN
      RAISE WARNING '#mne_lang#Gelieferte Menge ist grösser als bestellte Menge#';
     ELSE
      RAISE EXCEPTION '#mne_lang#Gelieferte Menge ist grösser als bestellte Menge#';
     END IF;
   END IF;
   
   IF r_purchase.count <= r_deliverysum.sum THEN
      RAISE NOTICE '#mne_lang#Gelieferte Menge ist vollständig#';
      UPDATE mne_warehouse.purchase SET deliverydate = acttime WHERE purchaseid = p_purchaseid;
   ELSE
      UPDATE mne_warehouse.purchase SET deliverydate = NULL WHERE purchaseid = p_purchaseid;
   END IF;
   
   r_partingoingid = p_partingoingid;
   IF p_partingoingid = '' AND p_storagelocationid <> '' THEN
       r_partingoingid = '################';
   END IF;
   
   IF p_storagelocationid <> '' THEN
       PERFORM mne_warehouse.partingoing_ok(r_partingoingid, result, p_partstoragelocationid, p_storagelocationid );
   END IF;
   
  PERFORM partvendorid FROM mne_warehouse.purchase WHERE purchaseid = p_purchaseid AND partvendorid = p_partvendorid;
   IF NOT FOUND THEN
     RAISE WARNING '#mne_lang#Lieferant wurde gewechselt#';
     UPDATE mne_warehouse.purchase SET partvendorid = NULLIF(p_partvendorid,'') WHERE purchaseid = p_purchaseid;
   END IF;
   
   return result;
END $$;


