CREATE FUNCTION mne_warehouse.purchaseinvoice_ok(p_purchaseinvoiceid character varying, p_documentnumber character varying, p_invoicenumber character varying, p_invoicedate integer, p_amount double precision, p_accountnumber character varying, p_bankcode character varying, p_remittee character varying, p_purpose character varying, p_ownaccount character varying, p_companyid character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE
    result varchar;
    acttime integer;
    r_documentnumber varchar;
    r_invoicedate int4;
    r_purpose varchar;
    
BEGIN
    
   SELECT INTO acttime
        CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

   IF p_invoicedate = 0 THEN
     r_invoicedate = acttime;
   ELSE
     r_invoicedate = p_invoicedate;
   END IF;
   
   IF p_purpose = '' THEN
     r_purpose = 'Rechungsnummer ' || p_invoicenumber;
   ELSE
     r_purpose = p_purpose;
   END IF;
   
   IF p_amount <= 0.0 THEN
     RAISE EXCEPTION '#mne_lang#Betrag ist kleiner 0.0#';
   END IF;
   
   IF p_purchaseinvoiceid = '################' THEN

     SELECT mne_catalog.mk_id() INTO result;
     IF  p_documentnumber = '' THEN
           SELECT to_char(MAX(CAST(COALESCE(NULLIF(documentnumber,''),'0') AS int8)) + 1, 'FM99999999999999999999') INTO r_documentnumber FROM mne_warehouse.purchaseinvoice;
           r_documentnumber = COALESCE(r_documentnumber,'1');
     ELSE
         r_documentnumber = p_documentnumber;
     END IF;
     
         amount, accountnumber, bankcode, remittee, purpose, ownaccount, companyid,
         createdate, createuser, modifydate, modifyuser )
     VALUES
       ( result, r_documentnumber, p_invoicenumber, r_invoicedate, 
         p_amount, p_accountnumber, p_bankcode, p_remittee, r_purpose, p_ownaccount, p_companyid,
         acttime, session_user, acttime, session_user );
   
   ELSE
       result = p_purchaseinvoiceid;
       UPDATE mne_warehouse.purchaseinvoice SET
         documentnumber = p_documentnumber,
         invoicenumber = p_invoicenumber,
         invoicedate = r_invoicedate,
         amount = p_amount,
         accountnumber = p_accountnumber,
         bankcode = p_bankcode,
         remittee = p_remittee,
         purpose = r_purpose,
         ownaccount = p_ownaccount,
         companyid = p_companyid,
         modifydate = acttime,
         modifyuser = session_user
      WHERE
         purchaseinvoiceid = result;
         
   END IF;
     
  return result;
  
END $$;


