CREATE FUNCTION mne_warehouse.purchaseinvoice_pay(p_purchaseinvoiceid character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE
    acttime integer;
    r_func varchar;
    r RECORD;
    r_purchaseinvoicepayid VARCHAR;
    
BEGIN
  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  FOUND := false;
  PERFORM * FROM mne_warehouse.purchaseinvoice WHERE purchaseinvoiceid = p_purchaseinvoiceid;
  IF FOUND = FALSE THEN
    RAISE EXCEPTION '#mne_lang#Die Rechnung existiert nicht in der Datenbank#';
  END IF;
  
  FOUND := FALSE;
  PERFORM * FROM mne_warehouse.purchaseinvoicepay WHERE purchaseinvoiceid = p_purchaseinvoiceid;
  IF FOUND = TRUE THEN
     RAISE EXCEPTION '#mne_lang#Die Rechnung ist schon zur Bezahlung frei gegeben#';
  END IF;
  
  FOUND := FALSE;
  PERFORM * FROM mne_warehouse.purchaseinvoicedelivery WHERE purchaseinvoiceid = p_purchaseinvoiceid;
  IF FOUND = FALSE THEN
     RAISE EXCEPTION '#mne_lang#Der Rechnung sind keine Lieferscheine zugeordnet#';
  END IF;
  
  SELECT INTO r_purchaseinvoicepayid mne_catalog.mk_id();
  
    
  UPDATE mne_warehouse.purchaseinvoice SET paytime = acttime WHERE purchaseinvoiceid = p_purchaseinvoiceid;
  
  FOUND := FALSE;
  SELECT funcschema || '.' || func INTO r_func FROM mne_application.customerfunctions WHERE customerfunction = 'warehouse_purchaseinvoicepay';
  
  IF FOUND = TRUE AND r_func <> '.' THEN
     EXECUTE 'SELECT ' || r_func || '(' || quote_literal(r_purchaseinvoicepayid) || ')';
  END IF;
  
  return p_purchaseinvoiceid;
  
END $$;


