--
-- Name: relocation_ok(character varying, character varying, character varying, character varying, integer, integer); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.relocation_ok(p_relocationid character varying, p_partstoragelocationid character varying, p_newstoragelocationid character varying, p_personid character varying, p_count integer, p_sequence integer) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE
    acttime integer;
    result varchar;

    r_partingoingid varchar;
    r_setcount int4;
    r_storecount int4;
    r_sequence int4;
    r_dispcount int4;
    
    r_partstoragelocationmasterdata mne_warehouse.partstoragelocationmasterdata%rowtype;

BEGIN
    
  SELECT INTO acttime
    CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  
  IF p_count = 0 THEN
    RAISE EXCEPTION '#mne_lang#Anzahl zum Umlagern ist 0#';
  END IF;
  
  FOUND = false;
  PERFORM partoutgoingid FROM mne_warehouse.partoutgoing WHERE partstoragelocationid = p_partstoragelocationid;
  IF FOUND THEN
    RAISE EXCEPTION '#mne_lang#Aus dem Ausgang darf nicht umgelagert werden#';
  END IF;

  FOUND = false;
  PERFORM relocationid FROM mne_warehouse.partoutgoing WHERE relocationid = p_relocationid;
  IF FOUND THEN
    RAISE EXCEPTION '#mne_lang#Umlageraufträge für Ausgangsware darf nicht verändert werden#';
  END IF;
   
  SELECT DISTINCT
    t0.count, t0.partingoingid, COALESCE(SUM(t1.count),0) + COALESCE(SUM(t2.count),0)
  INTO
    r_setcount, r_partingoingid, r_storecount
  FROM 
    mne_warehouse.partstoragelocation t0 
    LEFT JOIN mne_warehouse.relocation t1 ON ( t0.partstoragelocationid = t1.partstoragelocationid AND t1.relocationid != p_relocationid )
    LEFT JOIN mne_warehouse.partstoragelocation t2 ON ( t2.partingoingid = t0.partingoingid AND t0.partstoragelocationid <> t1.partstoragelocationid )
  WHERE
    t0.partstoragelocationid = p_partstoragelocationid
  GROUP BY
    t0.count, t0.partingoingid;
          
   IF r_setcount < p_count + r_storecount THEN
        RAISE EXCEPTION '#mne_lang#Anzahl zum Umlagern ist zu gross#';
    END IF;

  SELECT DISTINCT
   COALESCE(t4.maxcount,t6.maxcount) - COALESCE(t2.count,0) - COALESCE(t7.sum,0) AS dispcount
   INTO
   r_dispcount
  FROM
              mne_warehouse.partstoragelocation t0 
    LEFT JOIN mne_warehouse.storagelocation t1 ON ( t0.storagelocationid <> t1.storagelocationid ) 
    LEFT JOIN mne_warehouse.partstorageloction_sum t2 ON ( t1.storagelocationid = t2.storagelocationid)
    LEFT JOIN mne_warehouse.partstoragelocation t3 ON ( t1.storagelocationid = t3.storagelocationid)
    LEFT JOIN mne_warehouse.partstoragelocationmasterdata t4 ON ( t1.storagelocationtypid = t4.storagelocationtypid)
    LEFT JOIN mne_warehouse.storage t5 ON ( t1.storageid = t5.storageid ) 
    LEFT JOIN mne_warehouse.partstoragelocationmasterdata t6 ON ( t5.storagelocationtypid = t6.storagelocationtypid)
    LEFT JOIN mne_warehouse.relocation_partnewstoragelocationsum t7 ON ( t1.storagelocationid = t7.newstoragelocationid)
    LEFT JOIN mne_warehouse.relocation t8 ON ( t8.relocationid = p_relocationid ) 
  WHERE
      (( t0.partid = t3.partid OR t3.partid isnull )
  AND ( t0.partid = t4.partid OR t1.storagelocationtypid isnull AND t0.partid = t6.partid)
  AND ( t7.partid isnull OR t0.partid = t7.partid)
  AND ( t0.partstoragelocationid = p_partstoragelocationid )
  AND ( t1.storagelocationid = p_newstoragelocationid )
  AND ( (COALESCE(t4.maxcount,t6.maxcount) - COALESCE(t2.count,0) - COALESCE(t7.sum,0) + COALESCE(t8.count,0) ) >= p_count )) OR ( t1.storagelocationtypid = 'unversal') OR ( t1.storagelocationtypid IS NULL AND t5.storagelocationtypid = 'universal');
  
  IF NOT FOUND THEN
    RAISE EXCEPTION '#mne_lang#Anzahl passt nicht auf Ziellagerplatz#';
  END IF;
  
  IF p_sequence = 0 THEN
    SELECT MAX(sequence) + 1 INTO r_sequence FROM mne_warehouse.relocation WHERE partstoragelocationid = p_partstoragelocationid;
  ELSE
    r_sequence = p_sequence;
  END IF;
  
  r_sequence = COALESCE(r_sequence, 1);
  
  FOUND = FALSE;
  PERFORM relocationid FROM mne_warehouse.relocation WHERE partstoragelocationid = p_partstoragelocationid AND sequence = r_sequence AND relocationid <> p_relocationid;
  IF FOUND THEN
    RAISE EXCEPTION '#mne_lang#Bitte andere Laufnummer wählen#';
  END IF;
  
  IF p_relocationid = '################' THEN
    SELECT mne_catalog.mk_id() INTO result;
      
    FOUND := false;
    PERFORM DISTINCT
    t3.storagelocationid AS storagelocationid
    FROM 
      mne_warehouse.partstoragelocation t0 LEFT JOIN mne_warehouse.part t1 ON ( t0.partid = t1.partid)
      LEFT JOIN mne_warehouse.partstoragelocationmasterdata t2 ON ( t1.partid = t2.partid)
      LEFT JOIN mne_warehouse.storagelocation t3 ON ( true )
      LEFT JOIN mne_warehouse.partstoragelocation t4 ON ( t3.storagelocationid = t4.storagelocationid)
      LEFT JOIN mne_warehouse.storage t5 ON ( t3.storageid = t5.storageid)
    WHERE 
            ( t0.partstoragelocationid != t4.partstoragelocationid OR  t4.partstoragelocationid isnull )
        AND ((     t2.storagelocationtypid = COALESCE(t3.storagelocationtypid, t5.storagelocationtypid ) 
               AND t0.partid = COALESCE(t4.partid, t0.partid)
               AND t2.maxcount  - COALESCE(t4.count,0) >= p_count )
            OR 'universal' = COALESCE(t3.storagelocationtypid, t5.storagelocationtypid) )
        AND t3.storagelocationid = p_newstoragelocationid;
                   
    IF NOT FOUND THEN
      RAISE EXCEPTION '#mne_lang# Ware kann nicht in den ausgewählten Speicherplatz eingelagert werden#';
    END IF;
      
    IF r_setcount - r_storecount > p_count THEN
      RAISE NOTICE '#mne_lang#Teile Lagercharge#';
    END IF;
    
        createdate, modifydate, createuser, modifyuser )
    VALUES
      ( result, p_newstoragelocationid, p_partstoragelocationid, r_partingoingid, r_sequence, NULLIF(p_personid,''), p_count,
        acttime, acttime, session_user, session_user ); 
  ELSE
    result = p_relocationid;
    
    UPDATE mne_warehouse.relocation SET
      newstoragelocationid = p_newstoragelocationid,
      partstoragelocationid = p_partstoragelocationid,
      sequence = r_sequence,
      personid = NULLIF(p_personid,''),
      count = p_count,
      modifydate = acttime,
      modifyuser = session_user
    WHERE
      relocationid = p_relocationid;

  END IF;
    
  return result;
END $$;


ALTER FUNCTION mne_warehouse.relocation_ok(p_relocationid character varying, p_partstoragelocationid character varying, p_newstoragelocationid character varying, p_personid character varying, p_count integer, p_sequence integer) OWNER TO admindb;

--
-- Name: relocation_ready(character varying); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.relocation_ready(p_relocationid character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE
    acttime integer;
    result varchar;
    r_relocation mne_warehouse.relocation%rowtype;
    r_partstoragelocationid varchar;
    r_count integer;
    
BEGIN
  SELECT INTO acttime
    CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);

  SELECT mne_catalog.mk_id() INTO result;
  SELECT * INTO r_relocation from mne_warehouse.relocation WHERE relocationid = p_relocationid;
  
  FOUND = false;
  SELECT t3.partstoragelocationid
  INTO 
    r_partstoragelocationid
  FROM mne_warehouse.relocation t0 
     LEFT JOIN mne_warehouse.partstoragelocation t1 ON ( t0.partstoragelocationid = t1.partstoragelocationid )
     LEFT JOIN mne_warehouse.storagelocation t2 ON ( t0.newstoragelocationid = t2.storagelocationid )
     LEFT JOIN mne_warehouse.partstoragelocation t3 ON ( t2.storagelocationid = t3.storagelocationid )
  WHERE t3.partingoingid = t1.partingoingid 
      AND t3.partid = t1.partid
      AND t0.relocationid = p_relocationid; 
  
  IF FOUND THEN 
    SELECT count INTO r_count FROM mne_warehouse.partstoragelocation WHERE partstoragelocationid = r_partstoragelocationid;
    IF r_count > 0 THEN
      RAISE NOTICE '#mne_lang#Ergänze Bestand#';
    END IF;
    
    UPDATE mne_warehouse.partstoragelocation
    SET count = count + r_relocation.count
    WHERE partstoragelocationid = r_partstoragelocationid;
  
  ELSE
  
        createdate, createuser, modifydate, modifyuser )
      SELECT result, t0.newstoragelocationid, t1.partid, t1.partingoingid, t0.count,
             acttime, session_user, acttime, session_user
      FROM mne_warehouse.relocation t0
           LEFT JOIN mne_warehouse.partstoragelocation t1 ON ( t0.partstoragelocationid = t1.partstoragelocationid )
      WHERE t0.relocationid = p_relocationid;
  
  END IF;

  UPDATE mne_warehouse.partstoragelocation SET
    count = count -  r_relocation.count,
    modifyuser = session_user,
    modifydate = acttime
  WHERE
    partstoragelocationid = r_relocation.partstoragelocationid;
  
  UPDATE mne_warehouse.partoutgoing SET relocationid = NULL WHERE relocationid = p_relocationid;
  DELETE FROM mne_warehouse.relocation WHERE relocationid = p_relocationid;

  SELECT count INTO r_count FROM mne_warehouse.partstoragelocation WHERE partstoragelocationid = r_relocation.partstoragelocationid;
  IF r_count = 0 THEN
    UPDATE mne_warehouse.partingoing SET partstoragelocationid = null WHERE partstoragelocationid = r_relocation.partstoragelocationid;
    UPDATE mne_warehouse.partoutgoing SET spartstoragelocationid = null WHERE spartstoragelocationid = r_relocation.partstoragelocationid;
    DELETE FROM mne_warehouse.partstoragelocation WHERE partstoragelocationid = r_relocation.partstoragelocationid;
  END IF;
      
