--
-- Name: storagecreate_ok(character varying, integer, integer, integer, character varying, character varying, character varying); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.storagecreate_ok(p_storageid character varying, p_xcount integer, p_ycount integer, p_zcount integer, p_xsname character varying, p_ysname character varying, p_zsname character varying) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE
    acttime integer;
    r_x integer;  
    r_y integer;  
    r_z integer;
    r_ycount integer;
    r_zcount integer;
    r_name varchar;
    r_storagelocation RECORD;
BEGIN
    
  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  
  PERFORM * FROM mne_warehouse.storage WHERE storageid = p_storageid;
  IF NOT FOUND THEN
    RAISE EXCEPTION '#mne_lang#Lager ist unbekannt#';
  END IF;
  
  UPDATE mne_warehouse.storage
  SET
     xcount = p_xcount,
     ycount = NULLIF(p_ycount,0),
     zcount = NULLIF(p_zcount,0),
     
     xsname = p_xsname,
     ysname = p_ysname,
     zsname = p_zsname,
     
     modifydate = acttime,
     modifyuser = session_user
  WHERE
     storageid = p_storageid;
     
   
   r_ycount = COALESCE(NULLIF(p_ycount,0),1);
   r_zcount = COALESCE(NULLIF(p_zcount,0),1);
   
   FOR r_x in 1..p_xcount LOOP
    FOR r_y in 1..r_ycount LOOP
      FOR r_z in 1..r_zcount LOOP
      
      r_name = p_xsname || CAST (r_x AS VARCHAR);
      IF p_ysname <> '' THEN
       r_name = r_name || ' ' || p_ysname || r_y;
      END IF;
      IF p_zsname <> '' THEN
       r_name = r_name || ' ' || p_zsname || r_z;
      END IF;
      
      SELECT * INTO r_storagelocation FROM mne_warehouse.storagelocation 
      WHERE storageid = p_storageid AND x =  r_x AND y = CASE WHEN p_ycount = 0 THEN 0 ELSE r_y END AND z = CASE WHEN p_ycount = 0 THEN 0 ELSE r_z END;
      
      IF FOUND THEN
        UPDATE mne_warehouse.storagelocation
        SET
          name = r_name,
          modifydate = acttime,
          modifyuser = session_user
        WHERE storagelocationid = r_storagelocation.storagelocationid;
      ELSE
            createdate, createuser, modifydate, modifyuser )
        VALUES
          ( mne_catalog.mk_id(), p_storageid, null, r_name, r_x, CASE WHEN p_ycount = 0 THEN 0 ELSE r_y END, CASE WHEN p_ycount = 0 THEN 0 ELSE r_z END,
            acttime, session_user, acttime, session_user);
        END IF;
        
      END LOOP;
    END LOOP;
  END LOOP;
  
  return 'ok';

END $$;


ALTER FUNCTION mne_warehouse.storagecreate_ok(p_storageid character varying, p_xcount integer, p_ycount integer, p_zcount integer, p_xsname character varying, p_ysname character varying, p_zsname character varying) OWNER TO admindb;

--
-- Name: storageopt_effort(character varying, integer, integer); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.storageopt_effort(p_partid character varying, p_stocktime integer, p_releasetime integer) RETURNS double precision
    LANGUAGE plpgsql
    AS $$ DECLARE
r_func varchar;
r_result float8;
r_sum float8;

BEGIN

  r_result = 0.0;
  SELECT INTO r_func 'SELECT ' || funcschema || '.' || func || '(' || quote_literal(p_partid) || ',' || p_stocktime || ',' || p_releasetime || ')'
  FROM mne_application.customerfunctions WHERE customerfunction = 'warehouse_storageopteffort';
  IF FOUND THEN
    EXECUTE  r_func INTO r_result;
    return r_result;
  END IF;

  SELECT INTO r_sum COALESCE(SUM(count) / COUNT(count), 0)  from mne_warehouse.storageoptpart WHERE fetchdate > CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER) - ( 30 * 86400);
  SELECT INTO  r_result ( SUM(t0.count) / count(t0.count) - r_sum ) * CAST((p_stocktime + p_releasetime ) / 2.0 AS float8) * COALESCE(MAX(t1.effort),1.0)  
  FROM mne_warehouse.storageoptpart t0
    LEFT JOIN mne_warehouse.partstoragelocationmasterdata t1 ON ( t0.partid = t1.partid )
  WHERE t0.partid = p_partid AND t0.fetchdate > CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER) - ( 30 * 86400);
  return COALESCE(r_result,0.0);

return r_result;
END $$;


