--
-- Name: storageopt_ok(character varying, integer, integer, integer, integer, integer); Type: FUNCTION; Schema: mne_warehouse; Owner: admindb
--

CREATE FUNCTION mne_warehouse.storageopt_ok(p_storageid character varying, p_stockstart integer, p_releasestart integer, p_xtime integer, p_ytime integer, p_ztime integer) RETURNS character varying
    LANGUAGE plpgsql
    AS $$ DECLARE
    acttime integer;
    r_rtime integer;  
    r_stime integer;  
    r_storage RECORD;
    r RECORD;
BEGIN
    
  SELECT INTO acttime CAST(FLOOR(EXTRACT(EPOCH FROM CURRENT_TIMESTAMP)) AS INTEGER);
  
  SELECT * INTO r_storage FROM mne_warehouse.storage WHERE storageid = p_storageid;
  IF NOT FOUND THEN
    RAISE EXCEPTION '#mne_lang#Lager ist unbekannt#';
  END IF;
  
  PERFORM * FROM mne_warehouse.storageopt WHERE storageoptid = p_storageid;
  IF NOT FOUND THEN
        createdate, createuser, modifydate, modifyuser )
    VALUES
      ( p_storageid, p_xtime, p_ytime, p_ztime, p_stockstart, p_releasestart,
        acttime, session_user, acttime, session_user );
  ELSE
    UPDATE mne_warehouse.storageopt
    SET
     xtime = p_xtime,
     ytime = p_ytime,
     ztime = p_ztime,
     
     stockpos = p_stockstart,
     releasepos = p_releasestart,

     modifydate = acttime,
     modifyuser = session_user
    WHERE
     storageoptid = p_storageid;
  END IF;
  
  FOR r in SELECT * from mne_warehouse.storagelocation WHERE storageid = p_storageid ORDER BY x, y, z LOOP

      r_stime = abs( r.x  - p_stockstart )  * p_xtime + ( COALESCE(r.y,1) - 1) * p_ytime + (COALESCE(r.z,1) -1) * p_ztime;
      r_rtime = abs( r.x  - p_releasestart) * p_xtime + ( COALESCE(r.y,1) - 1) * p_ytime + (COALESCE(r.z,1) -1) * p_ztime;

      UPDATE mne_warehouse.storagelocation
      SET
        stocktime = r_stime,
        releasetime = r_rtime,
          
        modifydate = acttime,
        modifyuser = session_user
      WHERE storagelocationid = r.storagelocationid;

  END LOOP;
  
  return 'ok';

END $$;


ALTER FUNCTION mne_warehouse.storageopt_ok(p_storageid character varying, p_stockstart integer, p_releasestart integer, p_xtime integer, p_ytime integer, p_ztime integer) OWNER TO admindb;

