-- Name: persontime _RETURN; Type: RULE; Schema: mne_builddiary; Owner: admindb
--

CREATE OR REPLACE VIEW mne_builddiary.persontime AS
 SELECT DISTINCT t0.timeid,
    t1.personid,
    t4.orderid,
    t2.orderproducttimeid,
    min((t5.timeid)::text) AS bdtimeid,
    count(t5.timeid) AS count,
    t0.createdate,
    t0.modifydate,
    t0.createuser,
    t0.modifyuser
   FROM (((((mne_personnal."time" t0
     LEFT JOIN mne_crm.person t1 ON (((t0.userid)::text = (t1.personid)::text)))
     LEFT JOIN mne_personnal.orderproducttime t2 ON (((t0.orderproducttimeid)::text = (t2.orderproducttimeid)::text)))
     LEFT JOIN mne_crm.orderproduct t3 ON (((t2.orderproductid)::text = (t3.orderproductid)::text)))
     LEFT JOIN mne_crm."order" t4 ON (((t3.orderid)::text = (t4.orderid)::text)))
     LEFT JOIN mne_builddiary."time" t5 ON ((((t4.orderid)::text = (t5.orderid)::text) AND (((t5.start >= t0.start) AND (t5.start < (t0.start + t0.duration))) OR (((t5.start + t5.duration) > t0.start) AND ((t5.start + t5.duration) <= (t0.start + t0.duration))) OR ((t5.start <= t0.start) AND ((t5.start + t5.duration) >= (t0.start + t0.duration)))))))
  GROUP BY t0.timeid, t1.personid, t4.orderid, t2.orderproducttimeid;


