INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1352722723, 'Nelson IT', '<part><text size="n">Analyse der Grundlagen.</text></part><part><text size="n">Abstimmen der Zielvorstellungen auf die Randbedingungen, die insbesondere durch Raumordnung, Landesplanung, Bauleitplanung, Rahmenplanung sowie örtliche und überörtliche Fachplanungen vorgegeben sind.</text></part><part><text size="n">Untersuchen von Lösungsmöglichkeiten mit ihren Einflüssen auf bauliche und konstruktive Gestaltung, Zweckmäßigkeit, Wirtschaftlichkeit unter Beachtung der Umweltverträglichkeit.</text></part><part><text size="n">Beschaffen und Auswerten amtlicher Karten.</text></part><part><text size="n">Erarbeiten eines Planungskonzepts einschließlich Untersuchung der alternativen Lösungsmöglichkeiten nach gleichen Anforderungen mit zeichnerischer Darstellung und Bewertung unter Einarbeitung der Beiträge andrer an der  Planung fachlich Beteiligter</text><text size="n">.</text></part><part><text size="n">Bei Verkehrsanlagen: Überschlägige verkehrstechnische Bemessung der Verkehrsanlage;</text></part><part><text size="n">Ermitteln der Schallimmissionen von der Verkehrsanlage an kritischen Stellen nach Tabellenwerten;</text></part><part><text size="n">Untersuchen der möglichen  Schallschutzmaßnahmen, ausgenommen detaillierte schalltechnische Untersuchungen, insbesondere in komplexen Fällen.</text></part><part><text size="n">Klären und Erläutern der wesentlichen fachspezifischen Zusammenhänge, Vorgänge und Bedingungen</text><text size="n">.</text></part><part><text size="n">Vorverhandlungen mit Behörden und anderen an der Planung fachlich Beteiligten über die Genehmigungsfähigkeit, gegebenenfalls über die Bezuschussung und Kostenbeteiligung</text><text size="n">.</text></part><part><text size="n">Mitwirken beim Erläutern des Planungskonzepts gegenüber Bürgern und politischen Gremien.</text></part><part><text size="n">Überarbeiten des Planungskonzepts nach Bedenken und Anregungen</text><text size="n">.</text></part><part><text size="n">Bereitstellen von Unterlagen als Auszüge aus dem Vorentwurf zur Verwendung für ein Raumordnungsverfahren</text><text size="n">.</text></part><part><text size="n">Kostenschätzung.</text></part><part><text size="n">Zusammenstellen aller Vorplanungsergebnisse.</text></part>', 1352888665, 'Nelson IT', 'Vorlage 43/47 Leistungsphase 2', '50a0e9230000', '100432', false);
