INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1351500726, 'Nelson IT', '<part><text size="n">Bestandsaufnahme</text><text size="n">.</text></part><part><text size="n">Erfassen und Darlegen der Ziele der Raumordnung und Landesplanung, der beabsichtigten Planungen und Maßnahmen der Gemeinde und der Träger öffentlicher Belange</text><text size="n">.</text></part><part><text size="n">Darstellen des Zustands unter Verwendung hierzu vorliegender Fachbeiträge, insbesondere im Hinblick auf Topographie, vorhandene Bebauung und ihre Nutzung, Freiflächen und ihre Nutzung, Verkehrs-, Ver- und  Entsorgungsanlagen, Umweltverhältnisse, wasserwirtschaftliche Verhältnisse, Lagerstätten, Bevölkerung, gewerbliche Wirtschaft, land- und forstwirtschaftliche Struktur.</text></part><part><text size="n">Darstellen von Flächen, deren Böden erheblich mit umweltgefährdenden Stoffen belastet sind, soweit Angaben hierzu vorliegen.</text></part><part><text size="n">Kleinere Ergänzungen vorhandener Karten nach örtlichen Feststellungen unter Berücksichtigung aller Gegebenheiten, die auf die Planung von Einfluß sind.</text></part><part><text size="n">Beschreiben des Zustands mit statistischen Angaben im Text, in Zahlen sowie zeichnerischen oder graphischen Darstellungen, die den letzen Stand der Entwicklung zeigen.</text></part><part><text size="n">Örtliche Erhebungen.</text></part><part><text size="n">Erfassen von vorliegenden Äußerungen der Einwohner.</text></part><part><text size="n">Analyse des in der Bestandsaufnahme ermittelten und beschriebenen Zustands.</text></part><part><text size="n">Zusammenstellen und Gewichten der vorliegenden Fachprognosen über die voraussichtliche Entwicklung der Bevölkerung, der sozialen und kulturellen Einrichtungen, der gewerblichen Wirtschaft, der Land- und Forstwirtschaft,  des Verkehrs, der Ver- und Entsorgung und des Umweltschutzes in Abstimmung mit dem Auftra