INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1351506066, 'Nelson IT', '<part><text size="n">Ermitteln des Planungsbestands, wie die bestehenden Planungen und Maßnahmen der Gemeinde und der Stellen, die Träger öffentlicher Belange sind</text><text size="n">.</text></part><part><text size="n">Ermitteln des Zustands des Planbereichs, wie Topographie, vorhandene Bebauung und Nutzung, Freiflächen und Nutzung einschließlich Bepflanzungen, Verkehrs-, Ver- und Entsorgungsanlagen, Umweltverhältnisse, Baugrund,  wasserwirtschaftliche Verhältnisse, Denkmalschutz und Milieuwerte, Naturschutz, Baustrukturen, Gewässerflächen, Eigentümer, durch Begehungen, zeichnerische Darstellungen, Beschreibungen unter Verwendung von Beiträgen anderer an  der Planung fachlich Beteiligter. Die Ermittlungen sollen sich auf die Bestandsaufnahme gemäß Flächennutzungsplan und deren Fortschreibung und Ergänzung stützen beziehungsweise darauf aufbauen</text><text size="n">.</text></part><part><text size="n">Darstellen von Flächen, deren Böden erheblich mit umweltgefährdenden Stoffen belastet sind, soweit Angaben hierzu vorliegen</text><text size="n">.</text></part><part><text size="n">Örtliche Erhebungen</text><text size="n">.</text></part><part><text size="n">Erfassen von vorliegenden Äußerungen der Einwohner.</text></part><part><text size="n">Analyse des in der Bestandsaufnahme ermittelten und beschriebenen Zustands.</text></part><part><text size="n">Prognose der voraussichtlichen Entwicklung, insbesondere unter Berücksichtigung von Auswirkungen übergeordneter Planungen unter Verwendung von Beiträgen anderer an der Planung fachlich Beteiligter</text><text size="n">.</text></part><part><text size="n">Mitwirken beim Aufstellen von Zielen und Zwecken der Planung.</text></part>', 1352888391, 'Nelson IT', 'Vorlage 21 Leistungsphase 2', '508e58920000', '100212', false);
