INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1352441512, 'Nelson IT', '<part><text size="n">Grundsätzliche Lösung der wesentlichen Teile der Aufgabe mit sich wesentlich unterscheidenden Lösungen nach gleichen Anforderungen in Text und Karten mit Begründung. Darlegen der Flächenfunktionen und räumlichen Strukturen nach ökologischen und gestalterischen Gesichtspunkten, insbesondere</text></part><itemize><item><part><text size="n">Flächen mit Nutzungsbeschränkungen einschließlich notwendiger Nutzungsänderungen zur Erhaltung oder Verbesserung des Naturhaushalts oder des Landschafts-/Ortsbildes</text></part></item><item><part><text size="n">landschaftspflegerische Sanierungsbereiche</text></part></item><item><part><text size="n">Flächen für landschaftspflegerische Entwicklungs- und Gestaltungsmaßnahmen</text></part></item><item><part><text size="n">Flächen für Ausgleichs- und Ersatzmaßnahmen</text></part></item><item><part><text size="n">Schutzgebiete und -objekte, Freiräume</text></part></item><item><part><text size="n"> Flächen für landschaftspflegerische Maßnahmen in Verbindung mit sonstigen Nutzungen.</text></part></item></itemize><part><text size="n">Darlegen von Entwicklungs-, Schutz-, Gestaltungs- und Pflegemaßnahmen, insbesondere für</text></part><itemize><item><part><text size="n">Grünflächen, Anpflanzung und Erhaltung von Grünbeständen</text></part></item><item><part><text size="n">Sport-, Spiel- und Erholungsflächen</text></part></item><item><part><text size="n">Fußwegsysteme</text></part></item><item><part><text size="n"> Gehölzanpflanzungen zur Einbindung baulicher Anlagen in die Umgebung</text><text size="n">    Ortseingänge und Siedlungsränder - pflanzliche Einbindungen von öffentlichen Straßen und Plätzen</text></part></item><item><part><text size="n">klimatisch wichtige Freiflächen, Immissionsschutzmaßnahmen.</text></part></item></itemize><part><text size="n">Festlegen von Pflegemaßnahmen aus Gründen des Naturschutzes und der Landschaftspflege. Erhaltung und Verbesserung der natürlichen Selbstreinigungskraft von Gewässern. Erhaltung und Pflege von naturnahen Vegetationsbeständen. Bodenschützende Maßnahmen - Schutz vor Schadstoffeintrag. Vorschläge für Gehölzarten der potentiell natürlichen Vegetation, für Leitarten bei Bepflanzungen, für Befestigungsarten bei Wohnstraßen, Gehwegen, Plätzen, Parkplätzen, für Versickerungsflächen. Festlegen der zeitlichen Folge von Maßnahmen. Kostenschätzung für durchzuführende Maßnahmen. Hinweise auf weitere Aufgaben von Naturschutz und Landschaftspflege.Vorschläge für Inhalte, die für die Übernahme in andere Planungen, insbesondere in die Bauleitplanung, geeignet sind Beteiligung an der Mitwirkung von Verbänden nach § 29 des Bundesnaturschutzgesetzes.Berücksichtigen von Fachplanungen Mitwirken an der Abstimmung des Vorentwurfs mit der für Naturschutz und Landschaftspflege zuständigen Behörde.</text></part><part><text size="n">Abstimmen des Vorentwurfs mit dem Auftraggeber</text><text size="n">Grundsätzliche Lösung der wesentlichen Teile der Aufgabe mit sich wesentlich unterscheidenden Lösungen nach gleichen Anforderungen in Text und Karten mit Begründung.</text></part><part><text size="n"> </text></part><part><text size="n">                                    </text></part>', 1352888491, 'Nelson IT', 'Vorlage 29 Leistungsphase 3', '509c9ea80000', '100293', false);
