INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1347887223, 'Nelson IT', '<part><text size="n">Durcharbeiten des Planungskonzepts (stufenweise Erarbeitung einer zeichnerischen Lösung) unter Berücksichtigung städtebaulicher, gestalterischer, funktionaler, technischer, bauphysikalischer, wirtschaftlicher,  energiewirtschaftlicher (z.B. hinsichtlich rationeller Energieverwendung und der Verwendung erneuerbarer Energien) und landschaftsökologischer Anforderungen unter Verwendung der Beiträge anderer an der Planung fachlich  Beteiligter bis zum vollständigen Entwurf</text><text size="n">.</text></part><part><text size="n">Integrieren der Leistungen anderer an der Planung fachlich Beteiligter</text><text size="n">.</text></part><part><text size="n">Objektbeschreibung mit Erläuterung von Ausgleichs- und Ersatzmaßnahmen nach Maßgabe der naturschutzrechtlichen Eingriffsregelung</text><text size="n">.</text></part><part><text size="n">Zeichnerische Darstellung des Gesamtentwurfs, z.B. durchgearbeitete, vollständige Vorentwurfs- und/oder Entwurfszeichnungen (Maßstab nach Art und Größe des Bauvorhabens; bei Freianlagen im Maßstab 1:500 bis 1:100,  insbesondere mit Angaben zur Verbesserung der Biotopfunktion, zu Vermeidungs-, Schutz-, Pflege- und Entwicklungsmaßnahmen sowie zur differenzierten Bepflanzung; bei raumbildenden Ausbauten: im Maßstab 1:50 bis 1:20,  insbesondere mit Einzelheiten der Wandabwicklungen, Farb-, Licht- und Materialgestaltung), gegebenenfalls auch Detailpläne mehrfach wiederkehrender Raumgruppen; </text><text size="n">Verhandlungen mit Behörden und anderen an der Planung fachlich  Beteiligten über die Genehmigungsfähigkeit</text><text size="n">.</text></part><part><text size="n">Kostenberechnung nach DIN 276 oder nach dem wohnungsrechtlichen Berechnungsrecht</text><text size="n">.</text></part><part><text size="n">Zusammenfassen aller Entwurfsunterlagen.</tex