INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386140848, 'admindb', '<itemize><item><part><text size="n">Formulieren von örtlichen Zielen und Grundsätzen zum Schutz, zur Pflege und Entwicklung von Natur und Landschaft einschließlich Erholungsvorsorge</text></part></item><item><part><text size="n">Darlegen der angestrebten Flächenfunktionen und Flächennutzungen sowie der örtlichen Erfordernisse und Maßnahmen zur Umsetzung der konkretisierten Ziele des Naturschutzes und der Landschaftspflege</text></part></item><item><part><text size="n">Erarbeiten von Vorschlägen zur Übernahme in andere Planungen, insbesondere in die Bauleitpläne</text></part></item><item><part><text size="n">Hinweise auf Folgeplanungen und -maßnahmen</text></part></item><item><part><text size="n">Mitwirken bei der Beteiligung der nach den Bestimmungen des Bundesnaturschutzgesetzes anerkannten Verbände</text></part></item><item><part><text size="n">Mitwirken bei der Abstimmung der Vorläufigen Fassung mit der für Naturschutz und Landschaftspflege zuständigen Behörde</text></part></item><item><part><text size="n">Abstimmen der Vorläufigen Fassung mit dem Auftraggeber</text></part></item></itemize><part><text size="n"></text></part>', 1386594143, 'admindb', 'Vorlage 28 Leistungsphase 3', '529ed4b00000', '200283', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386141800, 'admindb', '<itemize><item><part><text size="n">Ermitteln und Beschreiben der planungsrelevanten Sachverhalte auf Grundlage vorhandener Unterlagen und Daten</text></part></item><item><part><text size="n">Bewerten der Landschaft nach den Zielen des Naturschutzes und der Landschaftspflege einschließlich der Erholungsvorsorge</text></part></item><item><part><text size="n">Zusammenfassendes Darstellen der Bestandsaufnahme und Bewertung in Text und Karte</text></part></item></itemize><part><text size="n"></text></part>', 1386594244, 'admindb', 'Vorlage 29 Leistungsphase 2', '529ed8680000', '200292', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386582930, 'admindb', '<itemize><item><part><text size="n">Analysieren der Grundlagen Mitwirken beim Abstimmen der Leistungen mit den Planungsbeteiligten</text></part></item><item><part><text size="n">Erarbeiten eines Planungskonzepts, dazu gehören zum Beispiel: Vordimensionieren der Systeme und maßbestimmenden Anlagenteile, Untersuchen von alternativen Lösungsmöglichkeiten bei gleichen Nutzungsanforderungen einschließlich Wirtschaftlichkeitsvorbetrachtung, zeichnerische Darstellung zur Integration in die Objektplanung unter Berücksichtigung exemplarischer Details, Angaben zum Raumbedarf</text></part></item><item><part><text size="n">Aufstellen eines Funktionsschemas bzw. Prinzipschaltbildes für jede Anlage</text></part></item><item><part><text size="n">Klären und Erläutern der wesentlichen fachübergreifenden Prozesse, Randbedingungen und Schnittstellen, Mitwirken bei der Integration der technischen Anlagen</text></part></item><item><part><text size="n">Vorverhandlungen mit Behörden über die Genehmigungsfähigkeit und mit den zu beteiligenden Stellen zur Infrastruktur</text></part></item><item><part><text size="n">Kostenschätzung nach DIN 276 (2. Ebene) und Terminplanung</text></part></item><item><part><text size="n">Zusammenfassen, Erläutern und Dokumentieren der Ergebnisse</text></part></item></itemize>', 1386599433, 'admindb', 'Vorlage 56 Leistungsphase 2', '52a593920000', '200562', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386235576, 'admindb', '<itemize><item><part><text size="n">Erarbeiten der Entwurfsplanung, unter weiterer Berücksichtigung der wesentlichen Zusammenhänge, Vorgaben und Bedingungen (zum Beispiel städtebauliche, gestalterische, funktionale, technische, wirtschaftliche, ökologische, soziale, öffentlich-rechtliche) auf der Grundlage der Vorplanung und als Grundlage für die weiteren Leistungsphasen und die erforderlichen öffentlich-rechtlichen Genehmigungen unter Verwendung der Beiträge anderer an der Planung fachlich Beteiligter. Zeichnungen nach Art und Größe des Objekts im erforderlichen Umfang und Detaillierungsgrad unter Berücksichtigung aller fachspezifischen Anforderungen, zum Beispiel bei Gebäuden im Maßstab 1:100, zum Beispiel bei Innenräumen im Maßstab 1:50 bis 1:20</text></part></item><item><part><text size="n">Bereitstellen der Arbeitsergebnisse als Grundlage für die anderen an der Planung fachlich Beteiligten sowie Koordination und Integration von deren Leistungen</text></part></item><item><part><text size="n">Objektbeschreibung</text></part></item><item><part><text size="n">Verhandlungen über die Genehmigungsfähigkeit</text></part></item><item><part><text size="n">Kostenberechnung nach DIN 276 und Vergleich mit der Kostenschätzung</text></part></item><item><part><text size="n">Fortschreiben des Terminplans</text></part></item><item><part><text size="n">Zusammenfassen, Erläutern und Dokumentieren der Ergebnisse</text></part></item></itemize>', 1386596723, 'admindb', 'Vorlage 35 Leistungsphase 3', '52a046b80000', '200353', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386141809, 'admindb', '<itemize><item><part><text size="n">Lösen der Planungsaufgabe und Erläutern der Ziele, Erfordernisse und Maßnahmen in Text und Karte</text></part></item><item><part><text size="n">Darlegen der angestrebten Flächenfunktionen und Flächennutzungen</text></part></item><item><part><text size="n">Darlegen von Gestaltungs-, Schutz-, Pflege- und Entwicklungsmaßnahmen</text></part></item><item><part><text size="n">Vorschläge zur Übernahme in andere Planungen, insbesondere in die Bauleitplanung</text></part></item><item><part><text size="n">Mitwirken bei der Abstimmung der vorläufigen Fassung mit der für den Naturschutz zuständigen Behörde</text></part></item><item><part><text size="n">Bearbeiten der naturschutzrechtlichen Eingriffsregelung </text></part></item><item><part><text size="n">Ermitteln und Bewerten der durch die Planung zu erwartenden Beeinträchtigungen des Naturhaushalts und des Landschaftsbildes nach Art, Umfang, Ort und zeitlichem Ablauf</text></part></item><item><part><text size="n">Erarbeiten von Lösungen zur Vermeidung oder Verminderung erheblicher Beeinträchtigungen des Naturhaushalts und des Landschaftsbildes in Abstimmung mit den an der Planung fachlich Beteiligten</text></part></item><item><part><text size="n">Ermitteln der unvermeidbaren Beeinträchtigungen</text></part></item><item><part><text size="n">Vergleichendes Gegenüberstellen von unvermeidbaren Beeinträchtigungen und Ausgleich und Ersatz einschließlich Darstellen verbleibender, nicht ausgleichbarer oder ersetzbarer Beeinträchtigungen</text></part></item><item><part><text size="n">Darstellen und Begründen von Maßnahmen des Naturschutzes und der Landschaftspflege, insbesondere Ausgleichs-, Ersatz-, Gestaltungs- und Schutzmaßnahmen sowie Maßnahmen zur Unterhaltung und rechtlichen Sicherung von Ausgleichs- und Ersatzmaßnahmen</text></part></item><item><part><text size="n">Integrieren ergänzender, zulassungsrelevanter Regelungen und Maßnahmen auf Grund des Natura 2000-Gebietsschutzes und der Vorschriften zum besonderen Artenschutz auf Grundlage vorhandener Unterlagen</text></part></item></itemize>', 1386594899, 'admindb', 'Vorlage 29 Leistungsphase 3', '529ed8710000', '200293', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386322247, 'admindb', '<itemize><item><part><text size="n">Aufstellen der prüffähigen statischen Berechnungen für das Tragwerk unter Berücksichtigung der vorgegebenen bauphysikalischen Anforderungen</text></part></item><item><part><text size="n">Bei Ingenieurbauwerken: Erfassen von normalen Bauzuständen</text></part></item><item><part><text size="n">Anfertigen der Positionspläne für das Tragwerk oder Eintragen der statischen Positionen, der Tragwerksabmessungen, der Verkehrslasten, der Art und Güte der Baustoffe und der Besonderheiten der Konstruktionen in die Entwurfszeichnungen des Objektplaners</text></part></item><item><part><text size="n">Zusammenstellen der Unterlagen der Tragwerksplanung zur Genehmigung</text></part></item><item><part><text size="n">Abstimmen mit Prüfämtern und Prüfingenieuren oder Eigenkontrolle</text></part></item><item><part><text size="n">Vervollständigen und Berichtigen der Berechnungen und Pläne</text></part></item></itemize>', 1386599285, 'admindb', 'Vorlage 52 Leistungsphase 4', '52a199470000', '200524', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386322263, 'admindb', '<itemize><item><part><text size="n">Ermitteln der Betonstahlmengen im Stahlbetonbau, der Stahlmengen im Stahlbau und der Holzmengen im Ingenieurholzbau als Ergebnis der Ausführungsplanung und als Beitrag zur Mengenermittlung des Objektplaners</text></part></item><item><part><text size="n">Überschlägiges Ermitteln der Mengen der konstruktiven Stahlteile und statisch erforderlichen Verbindungs- und Befestigungsmittel im Ingenieurholzbau</text></part></item><item><part><text size="n">Mitwirken beim Erstellen der Leistungsbeschreibung als Ergänzung zu den Mengenermittlungen als Grundlage für das Leistungsverzeichnis des Tragwerks</text></part></item></itemize>', 1386599347, 'admindb', 'Vorlage 52 Leistungsphase 6', '52a199570000', '200526', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386582943, 'admindb', '<itemize><item><part><text size="n">Durcharbeiten des Planungskonzepts (stufenweise Erarbeitung einer Lösung) unter Berücksichtigung aller fachspezifischen Anforderungen sowie unter Beachtung der durch die Objektplanung integrierten Fachplanungen, bis zum vollständigen Entwurf</text></part></item><item><part><text size="n">Festlegen aller Systeme und Anlagenteile</text></part></item><item><part><text size="n">Berechnen und Bemessen der technischen Anlagen und Anlagenteile, Abschätzen von jährlichen Bedarfswerten (z. B. Nutz-, End- und Primärenergiebedarf) und Betriebskosten; Abstimmen des Platzbedarfs für technische Anlagen und Anlagenteile; Zeichnerische Darstellung des Entwurfs in einem mit dem Objektplaner abgestimmten Ausgabemaßstab mit Angabe maßbestimmender Dimensionen Fortschreiben und Detaillieren der Funktions- und Strangschemata der Anlagen Auflisten aller Anlagen mit technischen Daten und Angaben zum Beispiel für Energiebilanzierungen Anlagenbeschreibungen mit Angabe der Nutzungsbedingungen</text></part></item><item><part><text size="n">Übergeben der Berechnungsergebnisse an andere Planungsbeteiligte zum Aufstellen vorgeschriebener Nachweise; Angabe und Abstimmung der für die Tragwerksplanung notwendigen Angaben über Durchführungen und Lastangaben (ohne Anfertigen von Schlitz- und Durchführungsplänen)</text></part></item><item><part><text size="n">Verhandlungen mit Behörden und mit anderen zu beteiligenden Stellen über die Genehmigungsfähigkeit</text></part></item><item><part><text size="n">Kostenberechnung nach DIN 276 (3. Ebene) und Terminplanung</text></part></item><item><part><text size="n">Kostenkontrolle durch Vergleich der Kostenberechnung mit der Kostenschätzung</text></part></item><item><part><text size="n">Zusammenfassen, Erläutern und Dokumentieren der Ergebnisse</text></part>