INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386316526, 'admindb', '<itemize><item><part><text size="n">Erarbeiten des Entwurfs auf Grundlage der Vorplanung durch zeichnerische Darstellung im erforderlichen Umfang und Detaillierungsgrad unter Berücksichtigung aller fachspezifischen Anforderungen, Bereitstellen der Arbeitsergebnisse als Grundlage für die anderen an der Planung fachlich Beteiligten sowie Integration und Koordination der Fachplanungen</text></part></item><item><part><text size="n">Erläuterungsbericht unter Verwendung der Beiträge anderer an der Planung fachlich Beteiligter</text></part></item><item><part><text size="n">fachspezifische Berechnungen ausgenommen Berechnungen aus anderen Leistungsbildern</text></part></item><item><part><text size="n">Ermitteln und Begründen der zuwendungsfähigen Kosten, Mitwirken beim Aufstellen des Finanzierungsplans sowie Vorbereiten der Anträge auf Finanzierung</text></part></item><item><part><text size="n">Mitwirken beim Erläutern des vorläufigen Entwurfs gegenüber Dritten an bis zu drei Terminen, Überarbeiten des vorläufigen Entwurfs auf Grund von Bedenken und Anregungen</text></part></item><item><part><text size="n">Vorabstimmen der Genehmigungsfähigkeit mit Behörden und anderen an der Planung fachlich Beteiligten</text></part></item><item><part><text size="n">Kostenberechnung einschließlich zugehöriger Mengenermittlung, Vergleich der Kostenberechnung mit der Kostenschätzung</text></part></item><item><part><text size="n">Ermitteln der wesentlichen Bauphasen unter Berücksichtigung der Verkehrslenkung und der Aufrechterhaltung des Betriebes während der Bauzeit</text></part></item><item><part><text size="n">Bauzeiten- und Kostenplan</text></part></item><item><part><text size="n">Zusammenfassen, Erläutern und Dokumentieren der Ergebnisse</text></part></item></itemize><part><text size="n"> </text></part>', 1386598124, 'a