INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386322232, 'admindb', '<itemize><item><part><text size="n">Analysieren der Grundlagen</text></part></item><item><part><text size="n">Beraten in statisch-konstruktiver Hinsicht unter Berücksichtigung der Belange der Standsicherheit, der Gebrauchsfähigkeit und der Wirtschaftlichkeit</text></part></item><item><part><text size="n">Mitwirken bei dem Erarbeiten eines Planungskonzepts einschließlich Untersuchung der Lösungsmöglichkeiten des Tragwerks unter gleichen Objektbedingungen mit skizzenhafter Darstellung, Klärung und Angabe der für das Tragwerk wesentlichen konstruktiven Festlegungen für zum Beispiel Baustoffe, Bauarten und Herstellungsverfahren, Konstruktionsraster und Gründungsart</text></part></item><item><part><text size="n">Mitwirken bei Vorverhandlungen mit Behörden und anderen an der Planung fachlich Beteiligten über die Genehmigungsfähigkeit</text></part></item><item><part><text size="n">Mitwirken bei der Kostenschätzung und bei der Terminplanung</text></part></item><item><part><text size="n">Zusammenfassen, Erläutern und Dokumentieren der Ergebnisse</text></part></item></itemize>', 1386599149, 'admindb', 'Vorlage 52 Leistungsphase 2', '52a199380000', '200522', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386322239, 'admindb', '<itemize><item><part><text size="n">Erarbeiten der Tragwerkslösung, unter Beachtung der durch die Objektplanung integrierten Fachplanungen, bis zum konstruktiven Entwurf mit zeichnerischer Darstellung</text></part></item><item><part><text size="n">Überschlägige statische Berechnung und Bemessung</text></part></item><item><part><text size="n">Grundlegende Festlegungen der konstruktiven Details und Hauptabmessungen des Tragwerks für zum Beispiel Gestaltung der tragenden Querschnitte, Aussparungen und Fugen; Ausbildung der Auflager- und Knotenpunkte sowie der Verbindungsmittel</text></part></item><item><part><text size="n">Überschlägiges Ermitteln der Betonstahlmengen im Stahlbetonbau, der Stahlmengen im Stahlbau und der Holzmengen im Ingenieurholzbau</text></part></item><item><part><text size="n">Mitwirken bei der Objektbeschreibung bzw. beim Erläuterungsbericht</text></part></item><item><part><text size="n">Mitwirken bei Verhandlungen mit Behörden und anderen an der Planung fachlich Beteiligten über die Genehmigungsfähigkeit</text></part></item><item><part><text size="n">Mitwirken bei der Kostenberechnung und bei der Terminplanung</text></part></item><item><part><text size="n">Mitwirken beim Vergleich der Kostenberechnung mit der Kostenschätzung</text></part></item><item><part><text size="n">Zusammenfassen, Erläutern und Dokumentieren der Ergebnisse</text></part></item></itemize>', 1386599238, 'admindb', 'Vorlage 52 Leistungsphase 3', '52a1993f0000', '200523', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386582961, 'admindb', '<itemize><item><part><text size="n">Erarbeiten der Ausführungsplanung auf Grundlage der Ergebnisse der Leistungsphasen 3 und 4 (stufenweise Erarbeitung und Darstellung der Lösung) unter Beachtung der durch die Objektplanung integrierten Fachplanungen bis zur ausführungsreifen Lösung</text></part></item><item><part><text size="n">Fortschreiben der Berechnungen und Bemessungen zur Auslegung der technischen Anlagen und Anlagenteile Zeichnerische Darstellung der Anlagen in einem mit dem Objektplaner abgestimmten Ausgabemaßstab und Detaillierungsgrad einschließlich Dimensionen (keine Montage- oder Werkstattpläne) Anpassen und Detaillieren der Funktions- und Strangschemata der Anlagen bzw. der GA-Funktionslisten Abstimmen der Ausführungszeichnungen mit dem Objektplaner und den übrigen Fachplanern</text></part></item><item><part><text size="n">Anfertigen von Schlitz- und Durchbruchsplänen</text></part></item><item><part><text size="n">Fortschreibung des Terminplans</text></part></item><item><part><text size="n">Fortschreiben der Ausführungsplanung auf den Stand der Ausschreibungsergebnisse und der dann vorliegenden Ausführungsplanung des Objektplaners, Übergeben der fortgeschriebenen Ausführungsplanung an die ausführenden Unternehmen</text></part></item><item><part><text size="n">Prüfen und Anerkennen der Montage- und Werkstattpläne der ausführenden Unternehmen auf Übereinstimmung mit der Ausführungsplanung</text></part></item></itemize><part><text size="n"></text></part>', 1386599607, 'admindb', 'Vorlage 56 Leistungsphase 5', '52a593b10000', '200565', false);
INSERT INTO mne_crm.product (createdate, createuser, description, modifydate, modifyuser, name, productid, productnumber, withworkingstep) VALUES (1386582968, 'admindb', '<itemize><item><part><text size="n">Ermitteln von Mengen als Grundlage für das Aufstellen von Leistungsverzeichnissen in Abstimmung mit Beiträgen anderer an der Planung fachlich Beteiligter</text></part></item><item><part><text size="n">Aufstellen der Vergabeunterlagen, insbesondere mit Leistungsverzeichnissen nach Leistungsbereichen, einschließlich der Wartungsleistungen auf Grundlage bestehender Regelwerke</text></part></item><item><part><text size="n">Mitwirken beim Abstimmen der Schnittstellen zu den Leistungsbeschreibungen der anderen an der Planung fachlich Beteiligten</text></part></item><item><part><text size="n">Ermitteln der Kosten auf Grundlage der vom Planer bepreisten Leistungsverzeichnisse</text></part></item><item><part><text size="n">Kostenkontrolle durch Vergleich der vom Planer bepreisten Leistungsverzeichnisse mit der Kostenberechnung</text></part></item><item><part><text size="n">Zusammenstellen der Vergabeunterlagen</text></part></item></itemize>', 1386599647, 'admindb', 'Vorlage 56 Leistungsphase 6', '52a593b80000', '200566', false);
